        // Sauvegarder l'image dans PromptoDYS
        if (currentProject.directory) {
          const imagePath = await saveImageToPromptoDys(file, currentProject.directory);
          if (imagePath && viewMode === 'wysiwyg' && editorRef.current) {
            editorRef.current.focus();

            // Convertir l'URL relative en Blob URL pour affichage imm√©diat
            const blobUrl = await convertPromptoDysUrlToBlob(imagePath, currentProject.directory);
            const imgHtml = `<img src="${blobUrl}" style="max-width: 100%; height: auto; margin: 0.5em 0;" alt="${file.name}" />`;
            document.execCommand('insertHTML', false, imgHtml);
            onFormatChange();
            console.log('‚úÖ Image sauvegard√©e dans PromptoDYS:', imagePath);
            console.log('‚úÖ Blob URL cr√©√©e pour affichage:', blobUrl);
            event.target.value = '';
            return;
          }
