        return;
      }
    } else if (!isPromptoDysSupported) {
      alert('‚ùå PromptoDYS non support√©.\nUtilisez Chrome, Edge ou un navigateur compatible avec File System Access API.');
      event.target.value = '';
      return;
    } else if (!isPromptoDysReady && !promptoDysWorkspace) {
      alert('‚ùå PromptoDYS non initialis√©.\nConnectez-vous d\'abord au workspace PromptoDYS.');
