import { useState, useCallback } from 'react';

const DB_NAME = 'EditorImageDB';
const DB_VERSION = 1;
const STORE_NAME = 'images';

export const useImageStorage = () => {
  const [db, setDb] = useState(null);
  const [blobToImageIdMap, setBlobToImageIdMap] = useState(new Map());

  // Ouvrir/cr√©er la base IndexedDB
  const initDB = useCallback(async () => {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open(DB_NAME, DB_VERSION);

      request.onerror = () => reject(request.error);
      request.onsuccess = () => resolve(request.result);

      request.onupgradeneeded = (event) => {
        const database = event.target.result;

        // Cr√©er le store si n√©cessaire
        if (!database.objectStoreNames.contains(STORE_NAME)) {
          const imageStore = database.createObjectStore(STORE_NAME, { keyPath: 'id' });
          imageStore.createIndex('fileName', 'fileName', { unique: false });
          imageStore.createIndex('timestamp', 'timestamp', { unique: false });
        }
      };
    });
  }, []);

  // Stocker une image blob dans IndexedDB
  const storeImage = useCallback(async (blobUrl, fileName = 'image') => {
    try {
      // Initialiser la DB si n√©cessaire
      const database = db || await initDB();
      if (!db) setDb(database);

      // R√©cup√©rer le blob depuis l'URL
      const response = await fetch(blobUrl);
      const blob = await response.blob();

      // G√©n√©rer un ID unique
      const imageId = `img_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

      // Pr√©parer les donn√©es √† stocker
      const imageData = {
        id: imageId,
        fileName: fileName,
        blob: blob,
        mimeType: blob.type,
        size: blob.size,
        timestamp: new Date().toISOString()
      };

      // √âcrire dans IndexedDB
      const transaction = database.transaction([STORE_NAME], 'readwrite');
      const store = transaction.objectStore(STORE_NAME);

      return new Promise((resolve, reject) => {
        const request = store.add(imageData);
        request.onsuccess = () => {
          console.log('‚úÖ Image stock√©e dans IndexedDB:', fileName, imageId);
          resolve(imageId);
        };
        request.onerror = () => reject(request.error);
      });

    } catch (error) {
      console.error('‚ùå Erreur stockage image IndexedDB:', error);
      throw error;
    }
  }, [db, initDB]);

  // R√©cup√©rer une image depuis IndexedDB et cr√©er une blob URL
  const loadImage = useCallback(async (imageId) => {
    try {
      // Initialiser la DB si n√©cessaire
      const database = db || await initDB();
      if (!db) setDb(database);

      const transaction = database.transaction([STORE_NAME], 'readonly');
      const store = transaction.objectStore(STORE_NAME);

      return new Promise((resolve, reject) => {
        const request = store.get(imageId);

        request.onsuccess = () => {
          const result = request.result;
          if (result) {
            // Cr√©er une blob URL pour l'affichage
            const blobUrl = URL.createObjectURL(result.blob);

            // Enregistrer le mapping blob‚ÜíimageId pour la conversion inverse
            setBlobToImageIdMap(prev => new Map(prev.set(blobUrl, imageId)));

            console.log('‚úÖ Image charg√©e depuis IndexedDB:', result.fileName, imageId);
            resolve({
              blobUrl,
              fileName: result.fileName,
              mimeType: result.mimeType,
              size: result.size
            });
          } else {
            console.warn('‚ö†Ô∏è Image non trouv√©e dans IndexedDB:', imageId);
            resolve(null);
          }
        };

        request.onerror = () => reject(request.error);
      });

    } catch (error) {
      console.error('‚ùå Erreur chargement image IndexedDB:', error);
      return null;
    }
  }, [db, initDB]);

  // Lister toutes les images stock√©es
  const listImages = useCallback(async () => {
    try {
      const database = db || await initDB();
      if (!db) setDb(database);

      const transaction = database.transaction([STORE_NAME], 'readonly');
      const store = transaction.objectStore(STORE_NAME);

      return new Promise((resolve, reject) => {
        const request = store.getAll();
        request.onsuccess = () => {
          const images = request.result.map(img => ({
            id: img.id,
            fileName: img.fileName,
            mimeType: img.mimeType,
            size: img.size,
            timestamp: img.timestamp
          }));
          resolve(images);
        };
        request.onerror = () => reject(request.error);
      });

    } catch (error) {
      console.error('‚ùå Erreur listage images IndexedDB:', error);
      return [];
    }
  }, [db, initDB]);

  // Supprimer une image
  const deleteImage = useCallback(async (imageId) => {
    try {
      const database = db || await initDB();
      if (!db) setDb(database);

      const transaction = database.transaction([STORE_NAME], 'readwrite');
      const store = transaction.objectStore(STORE_NAME);

      return new Promise((resolve, reject) => {
        const request = store.delete(imageId);
        request.onsuccess = () => {
          console.log('‚úÖ Image supprim√©e de IndexedDB:', imageId);
          resolve(true);
        };
        request.onerror = () => reject(request.error);
      });

    } catch (error) {
      console.error('‚ùå Erreur suppression image IndexedDB:', error);
      return false;
    }
  }, [db, initDB]);

  // Convertir les blob URLs vers indexed:// URLs pour sauvegarde
  const convertBlobUrlsToIndexed = useCallback((htmlContent) => {
    let convertedHtml = htmlContent;
    let conversionCount = 0;

    // Parcourir toutes les blob URLs dans le mapping
    blobToImageIdMap.forEach((imageId, blobUrl) => {
      // Chercher et remplacer cette blob URL par indexed://imageId
      const blobRegex = new RegExp(blobUrl.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'), 'g');
      const matches = convertedHtml.match(blobRegex);

      if (matches) {
        convertedHtml = convertedHtml.replace(blobRegex, `indexed://${imageId}`);
        conversionCount += matches.length;
      }
    });

    if (conversionCount > 0) {
      console.log(`üîÑ ${conversionCount} blob URL(s) converties vers indexed:// pour sauvegarde`);
    }

    return convertedHtml;
  }, [blobToImageIdMap]);

  // Nettoyer les images orphelines (non r√©f√©renc√©es dans le contenu)
  const cleanupOrphanImages = useCallback(async (htmlContent) => {
    try {
      const allImages = await listImages();
      const usedImageIds = [];

      // Extraire tous les IDs d'images r√©f√©renc√©es dans le HTML
      const indexedImageRegex = /indexed:\/\/(img_[^"'\s]+)/g;
      let match;
      while ((match = indexedImageRegex.exec(htmlContent)) !== null) {
        usedImageIds.push(match[1]);
      }

      // Supprimer les images non r√©f√©renc√©es
      let deletedCount = 0;
      for (const image of allImages) {
        if (!usedImageIds.includes(image.id)) {
          await deleteImage(image.id);
          deletedCount++;
        }
      }

      if (deletedCount > 0) {
        console.log(`üßπ ${deletedCount} image(s) orpheline(s) supprim√©e(s)`);
      }

      return deletedCount;

    } catch (error) {
      console.error('‚ùå Erreur nettoyage images IndexedDB:', error);
      return 0;
    }
  }, [listImages, deleteImage]);

  return {
    storeImage,
    loadImage,
    listImages,
    deleteImage,
    cleanupOrphanImages
  };
};
