import React, { useRef, useEffect, useState, useCallback } from 'react';
import { useEditor } from './hooks/useEditor';
import { useEelBridge } from './hooks/useEelBridge';
import { useThemeSettings } from './hooks/useThemeSettings';
import { useMathJax } from './hooks/useMathJax';
import { htmlToMarkdown, markdownToHtml } from './utils/markdownConverter';
// Removed PromptoDYS imports - using simple project management
import { EditorProvider } from './contexts/EditorContext';
import Toolbar from './components/Toolbar';
import Editor from './components/Editor';
import StatusBar from './components/StatusBar';
import ThemeSettings from './components/ThemeSettings';

const App = () => {

  const {
    content,
    setContent,
    viewMode,
    changeViewMode,
    currentFormat,
    setCurrentFormat,
    editorRef,
    handleInput,
    updateCurrentFormat,
    saveAppliedFormat,
    ignoreSelectionChangeRef,
    // Gestion de s√©lection d'images
    selectedImage,
    setSelectedImage,
    handleImageClick,
    handleEditorClick,
    handleImageKeyboard
  } = useEditor();

  // Hook pour param√®tres de th√®me
  const { 
    settings: themeSettings, 
    isSettingsOpen: isThemeSettingsOpen, 
    toggleSettings: toggleThemeSettings,
    backgroundOptions,
    textOptions,
    fontOptions,
    updateSetting,
    resetToDefaults
  } = useThemeSettings();

  // ‚ö° Int√©gration MathJax en local
  const { renderMath } = useMathJax();

  // √âtat de restauration supprim√© - √©diteur volatil uniquement

  // ‚ö° Int√©gration Eel pour support desktop
  useEelBridge(content, setContent, viewMode);




  // Charger et restaurer le contenu depuis IndexedDB (sauf si action utilisateur explicite)
  useEffect(() => {
    const loadContentFromIndexedDB = async () => {
      console.log('üì• Chargement du contenu depuis IndexedDB');
      setIsRestoring(true); // üö´ BLOQUER handleChange pendant restauration

      const doc = await loadDocument();
      if (doc && doc.content) {
        console.log('üìÑ Document trouv√©:', doc.lastModified);
        console.log('üìù Contenu sauvegard√©:', doc.content.substring(0, 100) + '...');

        // Convertir les images indexed:// vers blob: pour l'affichage
        const displayContent = await convertContentForDisplay(doc.content);
        console.log('üîÑ Contenu apr√®s conversion d\'affichage:', displayContent.substring(0, 100) + '...');

        // Normaliser le HTML (supprimer cette normalisation car elle n'existe pas)
        const normalizedContent = displayContent;
        console.log('üéØ Contenu final normalis√©:', normalizedContent.substring(0, 100) + '...');

        setContent(normalizedContent);

        console.log('‚úÖ Contenu charg√© et converti depuis IndexedDB');
      } else {
        console.log('‚ùå Aucun document trouv√© dans IndexedDB');
      }

      // üîì D√âBLOQUER handleChange apr√®s 200ms
      setTimeout(() => {
        setIsRestoring(false);
        console.log('üîì Flag isRestoring d√©sactiv√© - handleChange r√©activ√©');
      }, 200);
    };

    const loadContent = async () => {
      if (!isInitialized) return;

      // V√©rifier si c'est une action utilisateur explicite (sessionStorage)
      const explicitActionFlag = sessionStorage.getItem('explicitUserAction');
      console.log('üîç Flag explicitUserAction:', explicitActionFlag);

      if (explicitActionFlag === 'true') {
        console.log('üö´ Restauration IndexedDB ignor√©e - Action utilisateur explicite');
        sessionStorage.removeItem('explicitUserAction'); // Auto-nettoyage
        return;
      }

      console.log('‚úÖ Refresh d√©tect√© (F5) - Restauration depuis IndexedDB autoris√©e');

      await loadContentFromIndexedDB();
    };

    loadContent();
  }, [isInitialized, loadDocument, convertContentForDisplay, setContent]);

  const editorContextValue = {
    content,
    editorRef,
    currentFormat,
    onFormatChange: setCurrentFormat,
    onViewModeChange: changeViewMode,
    viewMode,
    changeViewMode,
    onThemeSettingsToggle: toggleThemeSettings,
    onContentChange: setContent,
    ignoreSelectionChangeRef
  };

  return (
    <EditorProvider value={editorContextValue}>
      <div className="h-screen bg-white w-full relative">
        {/* Removed PromptoDYS error messages */}


        {/* Zone d'√©dition principale avec scroll ind√©pendant */}
        <div className="editor-container">
          <div className="editor-scroll">
            <Editor 
              viewMode={viewMode}
              content={content}
              editorRef={editorRef}
              onInput={handleInput}
              onSelectionChange={updateCurrentFormat}
              currentFormat={currentFormat}
              selectedImage={selectedImage}
              onImageClick={handleImageClick}
              onEditorClick={handleEditorClick}
              onDeleteSelectedImage={handleImageKeyboard}
              isRestoring={isRestoring}
            />
          </div>
        </div>

        {/* Toolbar fix√©e en bas */}
        <div className="fixed bottom-8 left-0 right-0 bg-gray-50 border-t border-gray-200 px-5 py-2 z-10">
          <Toolbar />
        </div>

        {/* StatusBar tout en bas */}
        <div className="fixed bottom-0 left-0 right-0 bg-gray-100 border-t border-gray-300 px-5 py-1 z-10">
          <StatusBar 
            currentFormat={currentFormat}
            content={content}
            dysSettings={themeSettings}
          />
        </div>

        {/* Modal ThemeSettings */}
        <ThemeSettings 
          settings={themeSettings}
          isSettingsOpen={isThemeSettingsOpen}
          toggleSettings={toggleThemeSettings}
          backgroundOptions={backgroundOptions}
          textOptions={textOptions}
          fontOptions={fontOptions}
          updateSetting={updateSetting}
          resetToDefaults={resetToDefaults}
        />
      </div>
    </EditorProvider>
  );
};

export default App;
