import React, { useState } from 'react';
import { markdownToHtml, htmlToMarkdown } from '../utils/markdownConverter';
// Removed PromptoDYS imports - using simplified project management

const Toolbar = ({ 
  content,
  setContent,
  viewMode, 
  onViewModeChange,
  currentFormat,
  onFormatChange,
  editorRef,
  onThemeSettingsToggle,
  ignoreSelectionChangeRef
}) => {
  // IndexedDB supprimÃ© - Ã©diteur volatil uniquement

  // Ã‰tats pour la modal de sauvegarde
  const [showSaveModal, setShowSaveModal] = useState(false);
  const [projectName, setProjectName] = useState('MonProjet');
  const [isSaving, setIsSaving] = useState(false);

  // VÃ©rifier le support File System Access API
  const isFileSystemAccessSupported = () => {
    return 'showDirectoryPicker' in window && 'showOpenFilePicker' in window;
  };

  // Fonctions pour les fichiers Markdown
  const handleCopyMarkdown = async () => {
    const editor = document.querySelector('.editor-scroll .prose');
    if (!editor) return;

    const markdown = htmlToMarkdown(content);

    try {
      await navigator.clipboard.writeText(markdown);
      console.log('Markdown copiÃ© dans le presse-papier');
    } catch (err) {
      console.error('Erreur lors de la copie:', err);
    }
  };

  // GÃ©nÃ©rer un nom de fichier unique avec timestamp
  const generateImageFileName = (originalName, extension) => {
    const now = new Date();
    const timestamp = now.getFullYear().toString() + 
                     String(now.getMonth() + 1).padStart(2, '0') +
                     String(now.getDate()).padStart(2, '0') + '_' +
                     String(now.getHours()).padStart(2, '0') +
                     String(now.getMinutes()).padStart(2, '0') +
                     String(now.getSeconds()).padStart(2, '0');

    const baseName = originalName.replace(/\.[^/.]+$/, "") || 'image';
    return `${baseName}_${timestamp}.${extension}`;
  };

  // Extraire et sauvegarder les images blob
  const extractAndSaveImages = async (htmlContent, projectDir) => {
    console.log('ðŸ” extractAndSaveImages appelÃ©e avec HTML:', htmlContent.substring(0, 200) + '...');

    // Parser le HTML avec DOMParser
    const parser = new DOMParser();
    const doc = parser.parseFromString(htmlContent, 'text/html');

    // Trouver toutes les images avec src blob:
    const images = doc.querySelectorAll('img[src^="blob:"]');

    console.log('ðŸ” Nombre d\'images trouvÃ©es:', images.length);
    images.forEach((img, i) => {
      console.log(`ðŸ” Image ${i + 1}:`, img.src, '|', img.alt);
    });

    if (images.length === 0) {
      console.log('âŒ Aucune image trouvÃ©e - HTML:', htmlContent);
      return htmlContent; // Pas d'images Ã  traiter
    }

    // CrÃ©er le dossier images
    const imagesDir = await projectDir.getDirectoryHandle('images', { create: true });

    // DÃ©duplication : tracker les URLs dÃ©jÃ  traitÃ©es
    const processedUrls = new Set();
    const urlToFileName = new Map();

    for (const img of images) {
      const imageUrl = img.src;
      const altText = img.alt || 'image';

      // VÃ©rifier si cette URL a dÃ©jÃ  Ã©tÃ© traitÃ©e
      if (processedUrls.has(imageUrl)) {
        // RÃ©utiliser le nom de fichier existant
        const existingFileName = urlToFileName.get(imageUrl);
        img.src = `./images/${existingFileName}`;
        console.log('â™»ï¸ Image dupliquÃ©e rÃ©utilisÃ©e:', existingFileName);
        continue;
      }

      try {
        let blob;
        let extension = 'jpg';
        let imageData = null; // DÃ©claration de la variable dans la portÃ©e appropriÃ©e

        if (imageUrl.startsWith('blob:')) {
          // RÃ©cupÃ©rer l'image depuis l'URL blob
          const response = await fetch(imageUrl);
          blob = await response.blob();
          extension = blob.type.split('/')[1] || 'jpg';
        } else {
          // URLs externes (http/https) - pas de tÃ©lÃ©chargement local
          console.warn('âš ï¸ Image externe non tÃ©lÃ©chargÃ©e:', imageUrl);
          continue;
        }

        if (!blob) {
          console.error('âŒ Impossible de rÃ©cupÃ©rer l\'image:', imageUrl);
          continue;
        }

        // Utiliser le nom de l'image ou dÃ©faut
        const originalName = altText || 'image';
        const fileName = generateImageFileName(originalName, extension);

        // Sauvegarder l'image
        const imageHandle = await imagesDir.getFileHandle(fileName, { create: true });
        const writable = await imageHandle.createWritable();
        await writable.write(blob);
        await writable.close();

        // Marquer cette URL comme traitÃ©e et stocker le nom de fichier
        processedUrls.add(imageUrl);
        urlToFileName.set(imageUrl, fileName);

        // Remplacer le src dans l'Ã©lÃ©ment DOM
        const relativePath = `./images/${fileName}`;
        img.src = relativePath;

        console.log('âœ… Image sauvegardÃ©e:', fileName);
      } catch (imageError) {
        console.error('âŒ Erreur sauvegarde image:', imageError);
      }
    }

    // Retourner le HTML modifiÃ© depuis le DOM
    return doc.body.innerHTML;
  };

  // Trouver un nom de dossier disponible (gestion des collisions)
  const findAvailableDirectoryName = async (parentDir, baseName) => {
    let finalName = baseName;
    let counter = 1;

    while (true) {
      try {
        // Tenter d'accÃ©der au dossier (si Ã§a marche, il existe dÃ©jÃ )
        await parentDir.getDirectoryHandle(finalName);
        // Le dossier existe, essayer le suivant
        finalName = `${baseName} (${counter})`;
        counter++;
      } catch (error) {
        // Le dossier n'existe pas, on peut l'utiliser
        break;
      }
    }

    return finalName;
  };

  // Fonction pour ouvrir la modal de sauvegarde
  const handleSaveMarkdown = async () => {
    console.log('ðŸ”¥ DEBUG: handleSaveMarkdown appelÃ©e');

    if (!isFileSystemAccessSupported()) {
      console.log('âŒ DEBUG: File System Access API non supportÃ©e');
      alert('âŒ Votre navigateur ne supporte pas l\'accÃ¨s aux fichiers.');
      return;
    }
    console.log('âœ… DEBUG: File System Access API supportÃ©e');

    // Ouvrir la modal pour demander le nom du projet
    setShowSaveModal(true);
  };

  // Fonction pour effectuer la sauvegarde avec le nom choisi
  const performSave = async (chosenProjectName) => {
    setIsSaving(true);
    try {
      const htmlContent = editorRef.current?.innerHTML || content;
      console.log('ðŸ“ DEBUG: htmlContent rÃ©cupÃ©rÃ©:', htmlContent?.length, 'caractÃ¨res');
      console.log('ðŸ“ DEBUG: projectName choisi:', chosenProjectName);

      // SÃ©lectionner le rÃ©pertoire parent
      const parentDirectoryHandle = await window.showDirectoryPicker({
        mode: 'readwrite'
      });

      // Trouver un nom de dossier disponible (Ã©viter les collisions)
      const availableProjectName = await findAvailableDirectoryName(
        parentDirectoryHandle, 
        chosenProjectName.trim()
      );

      // CrÃ©er le dossier du projet avec le nom disponible
      const projectDir = await parentDirectoryHandle.getDirectoryHandle(
        availableProjectName, 
        { create: true }
      );

      // Extraire et sauvegarder les images, puis mettre Ã  jour le HTML
      const updatedHtmlContent = await extractAndSaveImages(htmlContent, projectDir);

      // Convertir en markdown avec les nouveaux chemins d'images
      const markdownContent = htmlToMarkdown(updatedHtmlContent);

      // Sauvegarder le fichier markdown (utiliser le nom disponible)
      const fileName = `${availableProjectName}.md`;
      const fileHandle = await projectDir.getFileHandle(fileName, { create: true });
      const writable = await fileHandle.createWritable();
      await writable.write(markdownContent);
      await writable.close();

      const imageCount = (updatedHtmlContent.match(/src=["']\.\/images\/[^"']+["']/g) || []).length;
      const message = imageCount > 0 
        ? `âœ… Projet sauvegardÃ© avec succÃ¨s :\n${fileName} + ${imageCount} image(s) dans ${availableProjectName}/`
        : `âœ… Projet sauvegardÃ© avec succÃ¨s :\n${fileName} dans ${availableProjectName}/`;

      alert(message);
      setShowSaveModal(false);
    } catch (error) {
      console.error('âŒ Erreur sauvegarde:', error);
      if (error.name === 'AbortError') {
        setShowSaveModal(false);
        return;
      }
      alert(`âŒ Erreur lors de la sauvegarde :\n${error.message}`);
    } finally {
      setIsSaving(false);
    }
  };

  // Fonction pour confirmer la sauvegarde avec le nom choisi
  const handleConfirmSave = () => {
    if (projectName.trim() === '') return;
    performSave(projectName);
  };

  const handleOpenProject = async () => {
    if (!isFileSystemAccessSupported()) {
      alert('âŒ Votre navigateur ne supporte pas l\'accÃ¨s aux fichiers.');
      return;
    }

    // Ã‰diteur volatil - pas de persistance

    try {
      // SÃ©lectionner le rÃ©pertoire du projet
      const directoryHandle = await window.showDirectoryPicker({
        mode: 'read'
      });

      // Chercher le fichier .md correspondant au nom du rÃ©pertoire
      const projectName = directoryHandle.name;
      const fileName = `${projectName}.md`;

      try {
        const fileHandle = await directoryHandle.getFileHandle(fileName);
        const file = await fileHandle.getFile();
        const content = await file.text();
        let htmlContent = markdownToHtml(content);

        // Charger les images locales et les convertir en blob URLs
        htmlContent = await loadLocalImages(htmlContent, directoryHandle);

        if (editorRef.current) {
          editorRef.current.innerHTML = htmlContent;
          // DÃ©clencher l'Ã©vÃ©nement de changement pour mettre Ã  jour l'Ã©tat
          const inputEvent = new Event('input', { bubbles: true });
          editorRef.current.dispatchEvent(inputEvent);
        }

        // Basculer en mode WYSIWYG aprÃ¨s le chargement
        onViewModeChange('wysiwyg');

        const imageCount = (htmlContent.match(/src=["']blob:[^"']+["']/g) || []).length;
        const message = imageCount > 0 
          ? `âœ… Projet ouvert avec succÃ¨s :\n${fileName} + ${imageCount} image(s) chargÃ©e(s)`
          : `âœ… Projet ouvert avec succÃ¨s :\n${fileName}`;

        alert(message);
      } catch (fileError) {
        // Si le fichier n'existe pas, proposer de le crÃ©er
        const createFile = window.confirm(`Le fichier ${fileName} n'existe pas dans ce rÃ©pertoire.\n\nVoulez-vous crÃ©er un nouveau projet ?`);
        if (createFile) {
          const newContent = `# ${projectName}\n\nContenu de votre projet...`;
          const htmlContent = markdownToHtml(newContent);

          if (editorRef.current) {
            editorRef.current.innerHTML = htmlContent;
            const inputEvent = new Event('input', { bubbles: true });
            editorRef.current.dispatchEvent(inputEvent);
          }

          // Basculer en mode WYSIWYG aprÃ¨s le chargement
          onViewModeChange('wysiwyg');
          alert(`ðŸ“ Nouveau projet crÃ©Ã© : ${projectName}\n\nUtilisez "Sauvegarder" pour l'enregistrer.`);
        }
      }
    } catch (error) {
      console.error('âŒ Erreur ouverture projet:', error);
      if (error.name === 'AbortError') {
        // L'utilisateur a annulÃ©
        return;
      }
      alert(`âŒ Erreur lors de l'ouverture du projet :\n${error.message}`);
    }
  };

  const loadLocalImages = async (htmlContent, projectDir) => {
    // Trouver toutes les images avec src="./images/*"
    const localImageRegex = /<img[^>]+src=["']\.\/(images\/[^"']+)["'][^>]*>/g;
    const matches = [...htmlContent.matchAll(localImageRegex)];

    if (matches.length === 0) {
      return htmlContent; // Pas d'images locales
    }

    let updatedHtml = htmlContent;

    try {
      // VÃ©rifier si le dossier images existe
      const imagesDir = await projectDir.getDirectoryHandle('images');

      for (const match of matches) {
        const [fullMatch, imagePath] = match;
        const fileName = imagePath.split('/')[1]; // RÃ©cupÃ©rer juste le nom du fichier

        try {
          // Charger l'image depuis le dossier
          const imageHandle = await imagesDir.getFileHandle(fileName);
          const imageFile = await imageHandle.getFile();

          // CrÃ©er une blob URL pour l'affichage
          const blobUrl = URL.createObjectURL(imageFile);

          // Remplacer dans le HTML
          updatedHtml = updatedHtml.replace(`"./${imagePath}"`, `"${blobUrl}"`);

          console.log('âœ… Image locale chargÃ©e:', fileName);
        } catch (imageError) {
          console.error('âŒ Image introuvable:', fileName, imageError);
        }
      }
    } catch (dirError) {
      console.log('â„¹ï¸ Dossier images non trouvÃ©');
    }

    return updatedHtml;
  };

  const handleLoadMarkdown = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const markdownContent = e.target.result;
        const htmlContent = markdownToHtml(markdownContent);

        if (editorRef.current) {
          editorRef.current.innerHTML = htmlContent;
          // DÃ©clencher l'Ã©vÃ©nement de changement pour mettre Ã  jour l'Ã©tat
          const inputEvent = new Event('input', { bubbles: true });
          editorRef.current.dispatchEvent(inputEvent);
        }

        // Basculer en mode WYSIWYG aprÃ¨s le chargement
        onViewModeChange('wysiwyg');
        alert('Fichier Markdown chargÃ© avec succÃ¨s !');
      } catch (error) {
        console.error('Erreur lors du chargement:', error);
        alert('Erreur lors du chargement du fichier');
      }
    };

    reader.readAsText(file);
    // Reset l'input pour permettre de recharger le mÃªme fichier
    event.target.value = '';
  };

  const colors = [
    { name: 'Noir', value: '#000000' },
    { name: 'Bleu', value: '#3399ff' },
    { name: 'Vert', value: '#00cc00' },
    { name: 'Rouge', value: '#ff0000' },
    { name: 'Orange', value: '#ff9900' },
    { name: 'Violet', value: '#ff00ff' }
  ];


  const execCommand = (command, value = null) => {
    console.log('ðŸš€ EXECCOMMAND START:', command, 'currentFormat AVANT:', currentFormat);
    console.log('ðŸ”’ ignoreSelectionChangeRef AVANT activation:', ignoreSelectionChangeRef.current);

    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    // ðŸ”¥ PROTECTION ACTIVÃ‰E AVANT TOUT (mÃªme focus)
    ignoreSelectionChangeRef.current = true;
    console.log('ðŸ”’ PROTECTION ACTIVÃ‰E - ignoreSelectionChangeRef:', ignoreSelectionChangeRef.current);

    editorRef.current.focus();

    // Traitement uniforme - Ã‰tat d'abord, DOM aprÃ¨s
    const newFormat = { ...currentFormat };

    if (command === 'bold') {
      newFormat.bold = !currentFormat.bold;
      document.execCommand('bold', false, null);
    } else if (command === 'italic') {
      newFormat.italic = !currentFormat.italic;
      document.execCommand('italic', false, null);
    } else if (command === 'normal') {
      // Ã‰tat Normal immÃ©diat - FORCE tout Ã  false
      newFormat.bold = false;
      newFormat.italic = false;
      newFormat.color = '#000000';
      newFormat.fontSize = '16px';
      newFormat.fontFamily = 'system-ui';
      newFormat.heading = null;
      newFormat.list = null;

      // Force dÃ©sactivation DOM - toujours exÃ©cuter mÃªme si dÃ©jÃ  off
      if (document.queryCommandState('bold')) {
        document.execCommand('bold', false, null);
      }
      if (document.queryCommandState('italic')) {
        document.execCommand('italic', false, null);
      }
      document.execCommand('removeFormat', false, null);
      document.execCommand('formatBlock', false, 'p');
    } else if (command === 'foreColor') {
      newFormat.color = value;
      document.execCommand('foreColor', false, value);
    } else {
      document.execCommand(command, false, value);
    }

    // Mise Ã  jour immÃ©diate de l'Ã©tat
    console.log('ðŸ“ APPEL onFormatChange avec newFormat:', newFormat);
    onFormatChange(newFormat);
    console.log('âœ… onFormatChange appelÃ©');

    // Pour le bouton Normal, forcer une deuxiÃ¨me mise Ã  jour aprÃ¨s DOM
    if (command === 'normal') {
      setTimeout(() => {
        onFormatChange({
          bold: false,
          italic: false,
          color: '#000000',
          fontSize: '16px',
          fontFamily: 'system-ui',
          heading: null,
          list: null
        });
      }, 10);
    }

    // DÃ©sactiver la protection aprÃ¨s dÃ©lai
    setTimeout(() => {
      console.log('ðŸ”“ DÃ‰SACTIVATION PROTECTION aprÃ¨s 100ms');
      ignoreSelectionChangeRef.current = false;
      console.log('ðŸ”“ ignoreSelectionChangeRef maintenant:', ignoreSelectionChangeRef.current);
    }, 100);

    console.log('ðŸš€ EXECCOMMAND END:', command);
  };

  const handleHeading = (level) => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    // Nettoyer le formatage existant avant d'appliquer le titre
    document.execCommand('removeFormat', false, null);

    // Appliquer le titre
    document.execCommand('formatBlock', false, level);

    onFormatChange({ bold: false, italic: false, color: '#000000', fontSize: '16px', fontFamily: 'system-ui', heading: level, list: null });
  };


  const handleRemoveList = () => {
    console.log('ðŸš€ handleRemoveList() appelÃ©e');
    console.log('ðŸ” viewMode:', viewMode, 'editorRef.current:', !!editorRef.current);

    if (viewMode !== 'wysiwyg' || !editorRef.current) {
      console.log('âŒ Fonction arrÃªtÃ©e - mode non WYSIWYG ou pas d\'Ã©diteur');
      return;
    }

    editorRef.current.focus();
    console.log('âœ… Focus appliquÃ© Ã  l\'Ã©diteur');

    // DÃ©tecter si dans une liste et la supprimer
    const selection = window.getSelection();
    console.log('ðŸ“ Selection:', selection);
    console.log('ðŸ“ rangeCount:', selection.rangeCount);

    if (selection.rangeCount > 0) {
      let element = selection.getRangeAt(0).commonAncestorContainer;
      console.log('ðŸ” Element initial:', element, 'nodeType:', element.nodeType, 'tagName:', element.tagName);

      // Remonter jusqu'Ã  trouver UL ou OL
      while (element && element.nodeType !== 1) {
        element = element.parentNode;
        console.log('â¬†ï¸ RemontÃ©e nodeType - element:', element?.tagName);
      }

      console.log('ðŸ” AprÃ¨s remontÃ©e nodeType - element:', element?.tagName);

      while (element && !['UL', 'OL', 'LI'].includes(element.tagName)) {
        element = element.parentNode;
        console.log('â¬†ï¸ Recherche UL/OL/LI - element:', element?.tagName);
      }

      console.log('ðŸŽ¯ Element final trouvÃ©:', element?.tagName);

      if (element && ['UL', 'OL', 'LI'].includes(element.tagName)) {
        console.log('âœ… Liste dÃ©tectÃ©e! Tagname:', element.tagName);
        console.log('ðŸ”§ Avant execCommand formatBlock');

        // Supprimer la liste en convertissant en paragraphe
        document.execCommand('formatBlock', false, 'p');
        console.log('ðŸ”§ AprÃ¨s execCommand formatBlock');

        console.log('ðŸ”§ currentFormat avant onFormatChange:', currentFormat);
        onFormatChange({ ...currentFormat, list: null });
        console.log('âœ… onFormatChange appelÃ© avec list: null');
      } else {
        console.log('âŒ Aucune liste trouvÃ©e - element:', element?.tagName);
      }
    } else {
      console.log('âŒ Aucune sÃ©lection active');
    }
  };

  const handleList = (type) => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    if (type === 'bullet') {
      document.execCommand('insertUnorderedList', false, null);
    } else if (type === 'number') {
      document.execCommand('insertOrderedList', false, null);
    } else if (type === 'letter') {
      document.execCommand('insertOrderedList', false, null);
