        console.log('âœ… Image volatile insÃ©rÃ©e:', file.name);

        // RÃ©initialiser l'input file
        event.target.value = '';

      } catch (error) {
        console.error('âŒ ERREUR DÃ‰TAILLÃ‰E insertion image:', {
