import React, { useEffect, useState } from 'react';
import { useEditor } from './hooks/useEditor';
import { useEelBridge } from './hooks/useEelBridge';
import { useThemeSettings } from './hooks/useThemeSettings';
import { useMathJax } from './hooks/useMathJax';
import { 
  isFileSystemAccessSupported, 
  initializePromptoDysWorkspace, 
  getLastWorkspaceName, 
  restorePromptoDysState, 
  ensureCurrentProject 
} from './utils/promptoDysManager';
import Toolbar from './components/Toolbar';
import Editor from './components/Editor';
import StatusBar from './components/StatusBar';
import ThemeSettings from './components/ThemeSettings';

const App = () => {
  const [promptoDysReady, setPromptoDysReady] = useState(false);
  const [promptoDysError, setPromptoDysError] = useState('');
  const [needsWorkspaceConnection, setNeedsWorkspaceConnection] = useState(false);
  const [isRestoringPromptoDYS, setIsRestoringPromptoDYS] = useState(true);

  const {
    content,
    setContent,
    viewMode,
    changeViewMode,
    currentFormat,
    setCurrentFormat,
    editorRef,
    handleInput,
    updateCurrentFormat,
    saveAppliedFormat
  } = useEditor();

  // Hook pour param√®tres de th√®me
  const { 
    settings: themeSettings, 
    isSettingsOpen: isThemeSettingsOpen, 
    toggleSettings: toggleThemeSettings,
    backgroundOptions,
    textOptions,
    fontOptions,
    updateSetting,
    resetToDefaults
  } = useThemeSettings();

  // ‚ö° Int√©gration MathJax en local
  const { renderMath } = useMathJax();

  // ‚ö° Int√©gration Eel pour support desktop
  useEelBridge(content, setContent, viewMode);

  // üóÇÔ∏è V√©rification au d√©marrage si PromptoDYS est support√©
  useEffect(() => {
    if (!isFileSystemAccessSupported()) {
      setPromptoDysError("‚ùå File System Access API non support√©. Utilisez Chrome, Edge ou un navigateur compatible.");
      setNeedsWorkspaceConnection(false);
    }
  }, []);

  // Fonction de connexion au workspace
  const connectToWorkspace = async () => {
    try {
      setPromptoDysError('');
      const workspace = await initializePromptoDysWorkspace();

      if (workspace) {
        // Cr√©er automatiquement un projet apr√®s connexion
        await ensureCurrentProject(workspace);

        setPromptoDysReady(true);
        setNeedsWorkspaceConnection(false);
        console.log('‚úÖ Workspace PromptoDYS connect√© avec projet');
      }
    } catch (error) {
      console.error('‚ùå Erreur connexion PromptoDYS:', error);
      setPromptoDysError('√âchec de la connexion au workspace PromptoDYS');
    }
  };

  // R√©cup√©ration du nom du workspace pr√©c√©dent
  const lastWorkspaceName = getLastWorkspaceName();

  // Sauvegarde automatique du contenu dans localStorage
  useEffect(() => {
    const savedContent = localStorage.getItem('editor-content');
    const savedMode = localStorage.getItem('editor-mode');

    if (savedContent) {
      setContent(savedContent);
    }
    if (savedMode && ['wysiwyg', 'html', 'markdown'].includes(savedMode)) {
      changeViewMode(savedMode);
    }

    // Restaurer PromptoDYS silencieusement
    const restorePromptoDYS = async () => {
      try {
        const { workspace, project } = await restorePromptoDysState();

        if (workspace) {
          setPromptoDysReady(true);
          setNeedsWorkspaceConnection(false);

          // Cr√©er projet automatiquement si pas de projet existant
          if (!project) {
            await ensureCurrentProject(workspace);
          }

          console.log('‚úÖ PromptoDYS restaur√© silencieusement');
        } else {
          // Premi√®re connexion n√©cessaire
          setNeedsWorkspaceConnection(true);
        }
      } catch (error) {
        console.error('‚ùå Erreur restauration PromptoDYS:', error);
        setNeedsWorkspaceConnection(true);
      } finally {
        setIsRestoringPromptoDYS(false);
      }
    };

    restorePromptoDYS();
  }, []);

  return (
    <div className="h-screen bg-white w-full relative">
      {/* Message d'erreur PromptoDYS */}
      {promptoDysError && (
        <div className="fixed top-4 left-1/2 transform -translate-x-1/2 bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded z-50 max-w-md text-center">
          <p className="text-sm">{promptoDysError}</p>
        </div>
      )}

      {/* Bouton de connexion PromptoDYS */}
      {!isRestoringPromptoDYS && needsWorkspaceConnection && (
        <div className="promptodys-connection">
          <button 
            onClick={connectToWorkspace}
            className="connect-button"
          >
            üóÇÔ∏è {getLastWorkspaceName() ? 'Reconnecter' : 'Configurer'} PromptoDYS
          </button>
          {getLastWorkspaceName() && (
            <p className="last-workspace">
              Dernier workspace : <strong>{getLastWorkspaceName()}</strong>
            </p>
          )}
          {promptoDysError && (
            <p className="error-message">{promptoDysError}</p>
          )}
        </div>
      )}

      {/* Zone d'√©dition principale avec scroll ind√©pendant */}
      <div className="editor-container">
        <div className="editor-scroll">
          <Editor 
            viewMode={viewMode}
            content={content}
            editorRef={editorRef}
            onInput={handleInput}
            onSelectionChange={updateCurrentFormat}
            currentFormat={currentFormat}
          />
        </div>
      </div>

      {/* Toolbar fix√©e en bas */}
      <div className="fixed bottom-8 left-0 right-0 bg-gray-50 border-t border-gray-200 px-5 py-2 z-10">
        <Toolbar 
          currentFormat={currentFormat}
          setCurrentFormat={setCurrentFormat}
          editorRef={editorRef}
          viewMode={viewMode}
          onViewModeChange={changeViewMode}
          onFormatChange={saveAppliedFormat}
          onThemeSettingsToggle={toggleThemeSettings}
          promptoDysReady={promptoDysReady}
        />
      </div>

      {/* StatusBar tout en bas */}
      <div className="fixed bottom-0 left-0 right-0 bg-gray-100 border-t border-gray-300 px-5 py-1 z-10">
        <StatusBar 
          currentFormat={currentFormat}
          content={content}
          dysSettings={themeSettings}
        />
      </div>

      {/* Modal ThemeSettings */}
      <ThemeSettings 
        settings={themeSettings}
        isSettingsOpen={isThemeSettingsOpen}
        toggleSettings={toggleThemeSettings}
        backgroundOptions={backgroundOptions}
        textOptions={textOptions}
        fontOptions={fontOptions}
        updateSetting={updateSetting}
        resetToDefaults={resetToDefaults}
      />
    </div>
  );
};

export default App;
