import React, { useRef, useEffect, useState, useCallback } from 'react';
import { useEditor } from './hooks/useEditor';
import { useEelBridge } from './hooks/useEelBridge';
import { useThemeSettings } from './hooks/useThemeSettings';
import { useMathJax } from './hooks/useMathJax';
import useIndexedStorage from './hooks/useIndexedStorage';
import { htmlToMarkdown, markdownToHtml } from './utils/markdownConverter';
// Removed PromptoDYS imports - using simple project management
import { EditorProvider } from './contexts/EditorContext';
import Toolbar from './components/Toolbar';
import Editor from './components/Editor';
import StatusBar from './components/StatusBar';
import ThemeSettings from './components/ThemeSettings';

const App = () => {
  // Removed PromptoDYS states - simplified project management

  const {
    content,
    setContent,
    viewMode,
    changeViewMode,
    currentFormat,
    setCurrentFormat,
    editorRef,
    handleInput,
    updateCurrentFormat,
    saveAppliedFormat,
    ignoreSelectionChangeRef,
    // Gestion de s√©lection d'images
    selectedImage,
    setSelectedImage,
    handleImageClick,
    handleEditorClick,
    handleDeleteSelectedImage
  } = useEditor();

  // Hook pour param√®tres de th√®me
  const { 
    settings: themeSettings, 
    isSettingsOpen: isThemeSettingsOpen, 
    toggleSettings: toggleThemeSettings,
    backgroundOptions,
    textOptions,
    fontOptions,
    updateSetting,
    resetToDefaults
  } = useThemeSettings();

  // ‚ö° Int√©gration MathJax en local
  const { renderMath } = useMathJax();

  // ‚ö° Int√©gration  // Hook pour stockage unifi√© IndexedDB
  const {
    saveDocument,
    loadDocument,
    saveImage,
    loadImage,
    convertContentForDisplay,
    isInitialized
  } = useIndexedStorage();

  // Extraire le contenu HTML BRUT du DOM (avec indexed:// uniquement)
  const extractRawHtmlForStorage = useCallback(() => {
    if (!editorRef.current) return content;

    // R√©cup√©rer le HTML comme string pour √©viter les requ√™tes r√©seau
    let htmlString = editorRef.current.innerHTML;

    // Nettoyer les wrappers resizable-image avant sauvegarde
    const tempDiv = document.createElement('div');
    tempDiv.innerHTML = htmlString;

    // Extraire les images des wrappers resizable-image
    const wrappers = tempDiv.querySelectorAll('.resizable-image');
    wrappers.forEach(wrapper => {
      const img = wrapper.querySelector('img');
      if (img && wrapper.parentNode) {
        // Remplacer le wrapper par l'image seule
        wrapper.parentNode.replaceChild(img, wrapper);
      }
    });

    htmlString = tempDiv.innerHTML;

    // R√©cup√©rer la Map centralis√©e des correspondances blob‚ÜíimageId
    const blobMappings = getAllBlobMappings();

    // Remplacer les data-image-id dans le HTML string
    htmlString = htmlString.replace(/src="([^"]+)"\s+data-image-id="([^"]+)"/g, (match, src, imageId) => {
      console.log('üîÑ Conversion stockage: data-image-id', imageId, '‚Üí', `indexed://${imageId}`);
      return `src="indexed://${imageId}"`;
    });

    // Remplacer les blob URLs dans le HTML string
    blobMappings.forEach((imageId, blobUrl) => {
      const escapedBlobUrl = blobUrl.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
      const regex = new RegExp(`src="${escapedBlobUrl}"`, 'g');
      if (htmlString.includes(blobUrl)) {
        htmlString = htmlString.replace(regex, `src="indexed://${imageId}"`);
        console.log('üîÑ Conversion stockage (legacy):', blobUrl, '‚Üí', `indexed://${imageId}`);
      }
    });

    return htmlString;
  }, [content, getAllBlobMappings]);

  // Auto-save du contenu dans IndexedDB
  const saveContent = useCallback(async () => {
    if (content && isInitialized) {
      try {
        await saveDocument(content, viewMode);
        console.log('üíæ Contenu sauvegard√© dans IndexedDB');
      } catch (error) {
        console.error('‚ùå Erreur sauvegarde IndexedDB:', error);
      }
    }
  }, [content, viewMode, saveDocument, isInitialized]);

  // Sauvegarde automatique avec d√©lai
  useEffect(() => {
    const timeoutId = setTimeout(saveContent, 1000);
    return () => clearTimeout(timeoutId);
  }, [saveContent]);

  // Sauvegarde imm√©diate sur upload d'image
  useEffect(() => {
    const handleImmediateSave = () => {
      console.log('üíæ Sauvegarde imm√©diate d√©clench√©e par upload d\'image');
      saveContent();
    };

    window.addEventListener('immediateContentSave', handleImmediateSave);
    return () => window.removeEventListener('immediateContentSave', handleImmediateSave);
  }, [saveContent]);

  // ‚ö° Int√©gration Eel pour support desktop
  useEelBridge(content, setContent, viewMode);

  // Removed PromptoDYS workspace logic - simplified project management

  // Charger et restaurer le contenu depuis IndexedDB
  useEffect(() => {
    const loadContent = async () => {
      if (!isInitialized) return;

      try {
        console.log('üì• Chargement du contenu depuis IndexedDB');
        const document = await loadDocument();

        if (document) {
          console.log('üìÑ Document trouv√©:', document.lastModified);

          // Convertir indexed:// vers blob: URLs pour l'affichage
          const displayContent = await convertContentForDisplay(document.htmlContent);

          // Normaliser le contenu
          const normalizedContent = markdownToHtml(htmlToMarkdown(displayContent));
          setContent(normalizedContent);

          // Restaurer le mode de vue
          if (document.viewMode && ['wysiwyg', 'html', 'markdown'].includes(document.viewMode)) {
            changeViewMode(document.viewMode);
          }

          console.log('‚úÖ Contenu charg√© et converti depuis IndexedDB');
        } else {
          console.log('‚ö†Ô∏è Aucun document trouv√© dans IndexedDB');
        }

      } catch (error) {
        console.error('‚ùå Erreur chargement IndexedDB:', error);
      }
    };

    loadContent();
  }, [isInitialized, loadDocument, convertContentForDisplay, setContent, changeViewMode]);

  // √âcouter l'√©v√©nement de vidage des caches pour recharger le contenu
  useEffect(() => {
    const handleCacheCleared = async () => {
      console.log('üîÑ DEBUG: Cache vid√© d√©tect√© - rechargement du contenu');
      const savedContent = localStorage.getItem('editor-content');

      if (savedContent && savedContent.includes('indexed://')) {
        console.log('üîÑ DEBUG: Rechargement avec conversion indexed:// apr√®s vidage cache');
        const convertedContent = await convertIndexedUrlsToBlob(savedContent);
        const normalizedContent = markdownToHtml(htmlToMarkdown(convertedContent));
        setContent(normalizedContent);
        console.log('‚úÖ Contenu recharg√© avec nouvelles blob URLs');
      }
    };

    window.addEventListener('cacheCleared', handleCacheCleared);

    return () => {
      window.removeEventListener('cacheCleared', handleCacheCleared);
    };
  }, [convertIndexedUrlsToBlob]);

  // Sauvegarde automatique du contenu √† chaque modification + sauvegarde imm√©diate
  const performSave = useCallback(async () => {
    // Extraire HTML brut avec indexed:// pour stockage permanent
    const contentToSave = extractRawHtmlForStorage();
    localStorage.setItem('editor-content', contentToSave);

    // Log uniquement si des images sont pr√©sentes
    if (contentToSave.includes('<img')) {
      console.log('üíæ Contenu sauv√© avec r√©f√©rences indexed://', contentToSave.includes('indexed://') ? '‚úÖ' : '‚ùå');
    }

    // Nettoyer les images orphelines p√©riodiquement
    if (Math.random() < 0.1) { // 10% de chance √† chaque sauvegarde
      await cleanupOrphanImages(contentToSave);
    }
  }, [extractRawHtmlForStorage, cleanupOrphanImages]);

  // Sauvegarde automatique normale
  useEffect(() => {
    performSave();
  }, [content, performSave]);

  // √âcouter l'√©v√©nement de sauvegarde imm√©diate (upload d'images)
  useEffect(() => {
    const handleImmediateSave = () => {
      console.log('üéÜ Sauvegarde imm√©diate d√©clench√©e par upload');
      performSave();
    };

    window.addEventListener('immediateContentSave', handleImmediateSave);

    return () => {
      window.removeEventListener('immediateContentSave', handleImmediateSave);
    };
  }, [performSave]);

  // Sauvegarde automatique du mode de vue
  useEffect(() => {
    localStorage.setItem('editor-mode', viewMode);
  }, [viewMode]);

  const editorContextValue = {
    content,
    editorRef,
    currentFormat,
    onFormatChange: setCurrentFormat,
    onViewModeChange: changeViewMode,
    viewMode,
    changeViewMode,
    onThemeSettingsToggle: toggleThemeSettings,
    onContentChange: setContent,
    ignoreSelectionChangeRef
  };

  return (
    <EditorProvider value={editorContextValue}>
      <div className="h-screen bg-white w-full relative">
        {/* Removed PromptoDYS error messages */}


        {/* Zone d'√©dition principale avec scroll ind√©pendant */}
        <div className="editor-container">
          <div className="editor-scroll">
            <Editor 
              viewMode={viewMode}
              content={content}
              editorRef={editorRef}
              onInput={handleInput}
              onSelectionChange={updateCurrentFormat}
              currentFormat={currentFormat}
              convertIndexedUrlsToBlob={convertIndexedUrlsToBlob}
              selectedImage={selectedImage}
              onImageClick={handleImageClick}
              onEditorClick={handleEditorClick}
              onDeleteSelectedImage={handleDeleteSelectedImage}
            />
          </div>
        </div>

        {/* Toolbar fix√©e en bas */}
        <div className="fixed bottom-8 left-0 right-0 bg-gray-50 border-t border-gray-200 px-5 py-2 z-10">
          <Toolbar />
        </div>

        {/* StatusBar tout en bas */}
        <div className="fixed bottom-0 left-0 right-0 bg-gray-100 border-t border-gray-300 px-5 py-1 z-10">
          <StatusBar 
            currentFormat={currentFormat}
            content={content}
            dysSettings={themeSettings}
          />
        </div>

        {/* Modal ThemeSettings */}
        <ThemeSettings 
          settings={themeSettings}
          isSettingsOpen={isThemeSettingsOpen}
          toggleSettings={toggleThemeSettings}
          backgroundOptions={backgroundOptions}
          textOptions={textOptions}
          fontOptions={fontOptions}
          updateSetting={updateSetting}
          resetToDefaults={resetToDefaults}
        />
      </div>
    </EditorProvider>
  );
};

export default App;
