import React from 'react';
import { htmlToMarkdown, markdownToHtml } from '../utils/markdownConverter';
// Removed PromptoDYS image manager
import { useEditor } from '../contexts/EditorContext';

const Toolbar = () => {
  const {
    content,
    editorRef,
    currentFormat,
    onFormatChange,
    onViewModeChange,
    viewMode,
    changeViewMode,
    onThemeSettingsToggle,
    onContentChange
  } = useEditor();

  // V√©rifier le support File System Access API
  const isFileSystemAccessSupported = () => {
    return 'showDirectoryPicker' in window && 'showOpenFilePicker' in window;
  };

  // Fonctions pour les fichiers Markdown
  const handleCopyMarkdown = async () => {
    const editor = document.querySelector('.editor-scroll .prose');
    if (!editor) return;

    const markdown = htmlToMarkdown(content);

    try {
      await navigator.clipboard.writeText(markdown);
      console.log('Markdown copi√© dans le presse-papier');
    } catch (err) {
      console.error('Erreur lors de la copie:', err);
    }
  };

  const handleSaveMarkdown = async () => {
    if (!isFileSystemAccessSupported()) {
      alert('‚ùå Votre navigateur ne supporte pas l\'acc√®s aux fichiers.');
      return;
    }

    try {
      const htmlContent = editorRef.current?.innerHTML || content;
      const markdownContent = htmlToMarkdown(htmlContent);

      // Demander le nom du projet (utilis√© pour le r√©pertoire et le fichier)
      const projectName = prompt(
        'Nom du projet :',
        'MonProjet'
      );

      // Annuler si l'utilisateur clique sur Cancel ou saisit un nom vide
      if (!projectName || projectName.trim() === '') {
        return;
      }

      // S√©lectionner le r√©pertoire de destination
      const directoryHandle = await window.showDirectoryPicker({
        mode: 'readwrite'
      });

      // Sauvegarder le fichier .md dans le r√©pertoire s√©lectionn√©
      const fileName = `${projectName.trim()}.md`;
      const fileHandle = await directoryHandle.getFileHandle(fileName, { create: true });
      const writable = await fileHandle.createWritable();
      await writable.write(markdownContent);
      await writable.close();

      alert(`‚úÖ Projet sauvegard√© avec succ√®s :\n${fileName} dans ${directoryHandle.name}`);
    } catch (error) {
      console.error('‚ùå Erreur sauvegarde:', error);
      if (error.name === 'AbortError') {
        // L'utilisateur a annul√©
        return;
      }
      alert(`‚ùå Erreur lors de la sauvegarde :\n${error.message}`);
    }
  };

  const handleOpenProject = async () => {
    if (!isFileSystemAccessSupported()) {
      alert('‚ùå Votre navigateur ne supporte pas l\'acc√®s aux fichiers.');
      return;
    }

    try {
      // S√©lectionner le r√©pertoire du projet
      const directoryHandle = await window.showDirectoryPicker({
        mode: 'read'
      });

      // Chercher le fichier .md correspondant au nom du r√©pertoire
      const projectName = directoryHandle.name;
      const fileName = `${projectName}.md`;

      try {
        const fileHandle = await directoryHandle.getFileHandle(fileName);
        const file = await fileHandle.getFile();
        const content = await file.text();
        const htmlContent = markdownToHtml(content);

        if (editorRef.current) {
          editorRef.current.innerHTML = htmlContent;
          // D√©clencher l'√©v√©nement de changement pour mettre √† jour l'√©tat
          const inputEvent = new Event('input', { bubbles: true });
          editorRef.current.dispatchEvent(inputEvent);
        }

        // Basculer en mode WYSIWYG apr√®s le chargement
        onViewModeChange('wysiwyg');
        alert(`‚úÖ Projet ouvert avec succ√®s :\n${fileName}`);
      } catch (fileError) {
        // Si le fichier n'existe pas, proposer de le cr√©er
        const createFile = window.confirm(`Le fichier ${fileName} n'existe pas dans ce r√©pertoire.\n\nVoulez-vous cr√©er un nouveau projet ?`);
        if (createFile) {
          const newContent = `# ${projectName}\n\nContenu de votre projet...`;
          const htmlContent = markdownToHtml(newContent);

          if (editorRef.current) {
            editorRef.current.innerHTML = htmlContent;
            const inputEvent = new Event('input', { bubbles: true });
            editorRef.current.dispatchEvent(inputEvent);
          }

          onViewModeChange('wysiwyg');
          alert(`üìù Nouveau projet cr√©√© : ${projectName}\n\nUtilisez "Sauvegarder" pour l'enregistrer.`);
        }
      }
    } catch (error) {
      console.error('‚ùå Erreur ouverture projet:', error);
      if (error.name === 'AbortError') {
        // L'utilisateur a annul√©
        return;
      }
      alert(`‚ùå Erreur lors de l'ouverture :\n${error.message}`);
    }
  };

  const handleLoadMarkdown = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const markdownContent = e.target.result;
        const htmlContent = markdownToHtml(markdownContent);

        if (editorRef.current) {
          editorRef.current.innerHTML = htmlContent;
          // D√©clencher l'√©v√©nement de changement pour mettre √† jour l'√©tat
          const inputEvent = new Event('input', { bubbles: true });
          editorRef.current.dispatchEvent(inputEvent);
        }

        // Basculer en mode WYSIWYG apr√®s le chargement
        onViewModeChange('wysiwyg');
        alert('Fichier Markdown charg√© avec succ√®s !');
      } catch (error) {
        console.error('Erreur lors du chargement:', error);
        alert('Erreur lors du chargement du fichier');
      }
    };

    reader.readAsText(file);
    // Reset l'input pour permettre de recharger le m√™me fichier
    event.target.value = '';
  };

  const colors = [
    { name: 'Noir', value: '#000000' },
    { name: 'Bleu', value: '#3399ff' },
    { name: 'Vert', value: '#00cc00' },
    { name: 'Rouge', value: '#ff0000' },
    { name: 'Orange', value: '#ff9900' },
    { name: 'Violet', value: '#ff00ff' }
  ];


  const execCommand = (command, value = null) => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();
    document.execCommand(command, false, value);

    // Mise √† jour imm√©diate de l'√©tat des boutons
    const newFormat = { ...currentFormat };

    if (command === 'bold') {
      newFormat.bold = !currentFormat.bold;
    } else if (command === 'italic') {
      newFormat.italic = !currentFormat.italic;
    } else if (command === 'foreColor') {
      newFormat.color = value;
    }

    // Forcer la mise √† jour imm√©diate
    onFormatChange(newFormat);

    // Sauvegarder l'√©tat pour les d√©buts de ligne
    onFormatChange(newFormat);
  };

  const handleHeading = (level) => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    // Nettoyer le formatage existant avant d'appliquer le titre
    document.execCommand('removeFormat', false, null);

    // Appliquer le titre
    document.execCommand('formatBlock', false, level);

    onFormatChange();
  };

  const handleTextFormat = () => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    // D√©sactiver les listes d'abord si elles sont actives
    if (document.queryCommandState('insertUnorderedList')) {
      document.execCommand('insertUnorderedList', false, null);
    }
    if (document.queryCommandState('insertOrderedList')) {
      document.execCommand('insertOrderedList', false, null);
    }

    // Force d√©sactivation gras/italique AVANT removeFormat
    if (document.queryCommandState('bold')) {
      document.execCommand('bold', false, null);
    }
    if (document.queryCommandState('italic')) {
      document.execCommand('italic', false, null);
    }

    // Forcer d'abord le retour au paragraphe normal AVANT removeFormat
    document.execCommand('formatBlock', false, 'p');

    // Puis nettoyer le formatage existant
    document.execCommand('removeFormat', false, null);

    // √âtat explicite Normal pour mise √† jour imm√©diate
    const newFormat = {
      bold: false,
      italic: false,
      color: '#000000',
      fontSize: '16px',
      fontFamily: 'system-ui',
      heading: null,
      list: null
    };

    onFormatChange(newFormat);
  };

  const handleList = (type) => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    if (type === 'bullet') {
      document.execCommand('insertUnorderedList', false, null);
    } else if (type === 'number') {
      document.execCommand('insertOrderedList', false, null);
    } else if (type === 'letter') {
      document.execCommand('insertOrderedList', false, null);
      // Appliquer le style alphab√©tique √† la liste cr√©√©e
      const selection = window.getSelection();
      if (selection.rangeCount > 0) {
        let element = selection.getRangeAt(0).commonAncestorContainer;
        while (element && element.nodeType !== 1) {
          element = element.parentNode;
        }
        while (element && element.tagName !== 'OL') {
          element = element.parentNode;
        }
        if (element && element.tagName === 'OL') {
          element.style.listStyleType = 'lower-alpha';
        }
      }
    }

    onFormatChange();
  };

  const handleClearAll = () => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    // S√©lectionner tout le contenu puis le supprimer
    document.execCommand('selectAll', false, null);
    document.execCommand('delete', false, null);

    onFormatChange();
  };

  const handleCopy = () => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();
    document.execCommand('copy', false, null);
  };

  const handlePaste = () => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();
    document.execCommand('paste', false, null);

    // Forcer la mise √† jour apr√®s collage
    setTimeout(() => onFormatChange(), 10);
  };

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file || !file.type.startsWith('image/')) {
      alert('Veuillez s√©lectionner un fichier image valide.');
      return;
    }

    // Ins√©rer l'image directement dans l'√©diteur
    if (viewMode === 'wysiwyg' && editorRef.current) {
      // Cr√©er une URL blob pour l'affichage de l'image
      const blobUrl = URL.createObjectURL(file);
      editorRef.current.focus();
      const imgHtml = `<img src="${blobUrl}" style="max-width: 100%; height: auto; margin: 0.5em 0;" alt="${file.name}" />`;
      document.execCommand('insertHTML', false, imgHtml);
      onFormatChange();
      console.log('‚úÖ Image ins√©r√©e:', file.name);
      event.target.value = '';
    }
  };


  return (
    <div className="border-4 border-blue-600 rounded-lg bg-blue-100 py-3 shadow-lg w-full">
      {/* Ligne 1 - Format + Couleurs + Mode */}
      <div className="flex items-center justify-start space-x-6 mb-2 w-full">
        {/* Format buttons */}
        <div className="flex items-center space-x-2">
          <span className="text-sm font-medium">Format:</span>
          <button
            onClick={() => handleTextFormat()}
            className={`px-2 py-0.5 text-xs rounded ${
              !currentFormat.bold && !currentFormat.italic && !currentFormat.heading && !currentFormat.list
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Normal
          </button>
          <button
            onClick={() => execCommand('bold')}
            className={`px-2 py-0.5 text-xs rounded font-bold ${
              currentFormat.bold
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Gras
          </button>
          <button
            onClick={() => execCommand('italic')}
            className={`px-2 py-0.5 text-xs rounded italic ${
              currentFormat.italic
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Italique
          </button>
        </div>

        {/* Colors (sans libell√©) */}
        <div className="flex space-x-1">
          {colors.map((color, index) => (
            <button
              key={index}
              onClick={() => execCommand('foreColor', color.value)}
              className={`w-4 h-4 rounded-full border-2 ${
                currentFormat.color === color.value
                  ? 'border-blue-500 ring-2 ring-blue-300'
                  : 'border-gray-300 hover:border-gray-500'
              }`}
              style={{ backgroundColor: color.value }}
              disabled={viewMode !== 'wysiwyg'}
              title={color.name}
            />
          ))}
        </div>


        {/* Mode selection menu */}
        <div className="flex items-center space-x-2 mb-4">
          <span className="text-sm font-medium text-gray-700">Mode:</span>
          <select
            value={viewMode}
            onChange={(e) => onViewModeChange(e.target.value)}
            className="px-3 py-1 text-sm rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          >
            <option value="wysiwyg">üìù √âditeur visuel</option>
            <option value="markdown">#Ô∏è‚É£ Code Markdown</option>
            <option value="html">üåê Code HTML</option>
          </select>
        </div>

        {/* Fichiers Markdown */}
        <div className="flex items-center space-x-2 mb-4">
          <span className="text-sm font-medium text-gray-700">Fichier:</span>
          <div className="relative group">
            <button
              onClick={handleSaveMarkdown}
              className="border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500 px-2 py-1 text-xs rounded"
              title="Sauvegarder le projet"
            >
              üíæ Sauvegarder
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Sauvegarder en .md
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={handleOpenProject}
              className="border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500 px-2 py-1 text-xs rounded"
              title="Ouvrir un projet"
            >
              üìÅ Ouvrir projet
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Charger un fichier .md
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={() => {
                if (editorRef.current) {
                  editorRef.current.innerHTML = '<h1>Nouveau projet</h1><p>Commencez √† √©crire...</p>';
                  const inputEvent = new Event('input', { bubbles: true });
                  editorRef.current.dispatchEvent(inputEvent);
                }
                onViewModeChange('wysiwyg');
                alert('‚úÖ Nouveau projet cr√©√©. Utilisez "Sauvegarder" pour l\'enregistrer.');
              }}
              className="border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500 px-2 py-1 text-xs rounded"
              title="Commencer un nouveau projet"
            >
              üÜï Nouveau
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Nouveau projet
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
        </div>

        {/* Bouton Th√®me */}
        <div className="flex items-center space-x-2 mb-4">
          <div className="relative group">
            <button
              onClick={onThemeSettingsToggle}
              className="px-3 py-1 text-sm rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500 font-medium"
            >
              üé® Th√®me
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Param√®tres du th√®me
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>

          {/* Section simplifi√©e - plus de connexion PromptoDYS */}
        </div>

      </div>

      {/* Ligne 2 - Titres + Listes + √âdition */}
      <div className="flex items-center justify-start space-x-6 w-full">
        {/* Headings */}
        <div className="flex items-center space-x-2">
          <span className="text-sm font-medium">Titres:</span>
          <button
            onClick={() => handleTextFormat()}
            className={`px-2 py-0.5 text-xs rounded ${
              !currentFormat.heading && !currentFormat.list
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Texte
          </button>
          <button
            onClick={() => handleHeading('h1')}
            className={`px-2 py-0.5 text-xs rounded ${
              currentFormat.heading === 'h1'
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Titre 1
          </button>
          <button
            onClick={() => handleHeading('h2')}
            className={`px-2 py-0.5 text-xs rounded ${
              currentFormat.heading === 'h2'
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Titre 2
          </button>
          <button
            onClick={() => handleHeading('h3')}
            className={`px-2 py-0.5 text-xs rounded ${
              currentFormat.heading === 'h3'
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Titre 3
          </button>
        </div>

        {/* Lists */}
        <div className="flex items-center space-x-2">
          <span className="text-sm font-medium">Listes:</span>
          <button
            onClick={() => handleList('bullet')}
            className={`px-2 py-0.5 text-xs rounded ${
              currentFormat.list === 'bullet'
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            ‚Ä¢ Puces
          </button>
          <button
            onClick={() => handleList('number')}
            className={`px-2 py-0.5 text-xs rounded ${
              currentFormat.list === 'number'
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            1. Num√©ros
          </button>
          <button
            onClick={() => handleList('letter')}
            className={`px-2 py-0.5 text-xs rounded ${
              currentFormat.list === 'letter'
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            a. Lettres
          </button>
        </div>

        {/* Boutons Undo/Redo + Copier */}
        <div className="flex items-center space-x-2">
          <span className="text-sm font-medium text-gray-700">√âdition:</span>
          <div className="relative group">
            <button
              onClick={() => document.execCommand('undo', false, null)}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
              disabled={viewMode !== 'wysiwyg'}
            >
              ‚Ü∂
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Annuler (Ctrl+Z)
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={() => document.execCommand('redo', false, null)}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
              disabled={viewMode !== 'wysiwyg'}
            >
              ‚Ü∑
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              R√©tablir (Ctrl+Y)
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={handleCopy}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
              disabled={viewMode !== 'wysiwyg'}
            >
              üìÑ
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Copier (Ctrl+C)
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={handlePaste}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
              disabled={viewMode !== 'wysiwyg'}
            >
              üìã
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Coller (Ctrl+V)
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={() => document.getElementById('image-input').click()}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
              disabled={viewMode !== 'wysiwyg'}
            >
              üì∑
            </button>
            <input
              id="image-input"
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="hidden"
            />
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Ins√©rer une image
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={handleClearAll}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
              disabled={viewMode !== 'wysiwyg'}
            >
              üßΩ
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Effacer tout
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={handleCopyMarkdown}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
            >
              üìù
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Copier en Markdown
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Toolbar;
