import React, { useState } from 'react';
import { markdownToHtml, htmlToMarkdown } from '../utils/markdownConverter';
import { saveImage, loadImage } from '../utils/imageStore';
// Removed PromptoDYS imports - using simplified project management

const Toolbar = ({ 
  content, 
  setContent, 
  viewMode, 
  onViewModeChange, 
  currentFormat, 
  onFormatChange, 
  editorRef,
  onThemeSettingsToggle,
  ignoreSelectionChangeRef,
  storeBlobForUrl,
  getBlobFromUrl,
  getAllBlobs
}) => {
  // IndexedDB supprimÃ© - Ã©diteur volatil uniquement

  // Ã‰tats pour la modal de sauvegarde
  const [showSaveModal, setShowSaveModal] = useState(false);
  const [documentName, setDocumentName] = useState('MonDocument');
  const [isSaving, setIsSaving] = useState(false);

  // VÃ©rifier le support File System Access API
  const isFileSystemAccessSupported = () => {
    return 'showDirectoryPicker' in window && 'showOpenFilePicker' in window;
  };

  // Fonctions pour les fichiers Markdown
  const handleCopyMarkdown = async () => {
    const editor = document.querySelector('.editor-scroll .prose');
    if (!editor) return;

    const markdown = htmlToMarkdown(content);

    try {
      await navigator.clipboard.writeText(markdown);
      console.log('Markdown copiÃ© dans le presse-papier');
    } catch (err) {
      console.error('Erreur lors de la copie:', err);
    }
  };

  // CrÃ©er un blob unique pour Ã©viter les conflits d'URL
  const createUniqueBlob = (file, storeBlobForUrl) => {
    const uniqueId = `${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    const uniqueName = `${uniqueId}_${file.name}`;
    const uniqueFile = new File([file], uniqueName, { type: file.type });
    const blobUrl = URL.createObjectURL(uniqueFile);

    console.log('ðŸ”— URL blob unique crÃ©Ã©e:', blobUrl);

    // Stocker le blob pour rÃ©cupÃ©ration ultÃ©rieure
    if (storeBlobForUrl) {
      storeBlobForUrl(blobUrl, uniqueFile);
    }

    return { blobUrl, uniqueFile };
  };

  // GÃ©nÃ©rer un nom de fichier unique avec timestamp
  const generateImageFileName = (originalName, extension) => {
    const now = new Date();
    const timestamp = now.getFullYear().toString() + 
                     String(now.getMonth() + 1).padStart(2, '0') +
                     String(now.getDate()).padStart(2, '0') + '_' +
                     String(now.getHours()).padStart(2, '0') +
                     String(now.getMinutes()).padStart(2, '0') +
                     String(now.getSeconds()).padStart(2, '0');

    const baseName = originalName.replace(/\.[^/.]+$/, "") || 'image';
    return `${baseName}_${timestamp}.${extension}`;
  };

  // Fonction mutualisÃ©e pour traiter les images collÃ©es et les rendre uniques
  const processImageBlobs = async (container, storeBlobForUrl) => {
    console.log('ðŸ”„ [processImageBlobs] Traitement des images...');

    const images = container.querySelectorAll('img[src^="blob:"]');
    console.log('ðŸ” [processImageBlobs] Images blob trouvÃ©es:', images.length);

    for (const img of images) {
      const originalUrl = img.src;

      try {
        console.log('ðŸ“¥ [processImageBlobs] Traitement de:', originalUrl);

        // RÃ©cupÃ©rer le blob original
        const response = await fetch(originalUrl);
        const blob = await response.blob();

        // CrÃ©er un nouveau fichier unique Ã  partir du blob
        const filename = `image_${Date.now()}.${blob.type.split('/')[1] || 'png'}`;
        const uniqueFile = new File([blob], filename, { type: blob.type });

        // CrÃ©er un nouveau blob URL unique
        const { blobUrl } = createUniqueBlob(uniqueFile, storeBlobForUrl);

        // Remplacer l'URL dans l'image
        img.src = blobUrl;
        console.log('âœ… [processImageBlobs] Image mise Ã  jour:', originalUrl, '->', blobUrl);

      } catch (error) {
        console.error('âŒ [processImageBlobs] Erreur pour:', originalUrl, error);
      }
    }
  };

  // Validation prÃ©-sauvegarde des images blob (mise Ã  jour pour IndexedDB)
  const validateImagesBeforeSave = (htmlContent) => {
    console.log('ðŸ” [VALIDATION] DÃ©but validation des images avant sauvegarde...');

    // Parser le HTML avec DOMParser
    const parser = new DOMParser();
    const doc = parser.parseFromString(htmlContent, 'text/html');

    // Trouver toutes les images avec src blob:
    const blobImages = doc.querySelectorAll('img[src^="blob:"]');
    // Trouver les images persistantes avec data-image-id
    const persistentImages = doc.querySelectorAll('img[data-image-id]');

    console.log('ðŸ” [VALIDATION] Images blob trouvÃ©es:', blobImages.length);
    console.log('ðŸ” [VALIDATION] Images persistantes (IndexedDB) trouvÃ©es:', persistentImages.length);

    // Les images persistantes sont toujours valides
    if (blobImages.length === 0) {
      console.log('âœ… [VALIDATION] Aucune image blob temporaire Ã  valider');
      console.log('âœ… [VALIDATION] Images persistantes OK, sauvegarde autorisÃ©e');
      return { valid: true, missingBlobs: [], totalImages: blobImages.length + persistentImages.length };
    }

    // RÃ©cupÃ©rer les blobs disponibles en mÃ©moire (ancien systÃ¨me)
    const availableBlobs = getAllBlobs ? getAllBlobs() : new Map();
    const storedBlobUrls = Array.from(availableBlobs.keys());
    console.log('ðŸ” [VALIDATION] Blobs temporaires en mÃ©moire:', storedBlobUrls.length);
    console.log('ðŸ” [VALIDATION] URLs temporaires stockÃ©es:', storedBlobUrls);

    // VÃ©rifier seulement les images blob: temporaires
    const missingBlobs = [];
    blobImages.forEach((img, index) => {
      const imageUrl = img.src;
      console.log(`ðŸ” [VALIDATION] VÃ©rification image blob ${index + 1}:`, imageUrl);

      if (availableBlobs.has(imageUrl)) {
        const storedFile = availableBlobs.get(imageUrl);
        console.log(`âœ… [VALIDATION] Blob temporaire trouvÃ© pour image ${index + 1}:`, storedFile?.name, `(${storedFile?.size} bytes)`);
      } else {
        console.log(`âŒ [VALIDATION] Blob temporaire MANQUANT pour image ${index + 1}:`, imageUrl);
        missingBlobs.push({
          index: index + 1,
          url: imageUrl,
          alt: img.alt || 'image sans nom'
        });
      }
    });

    const isValid = missingBlobs.length === 0;

    if (isValid) {
      console.log('âœ… [VALIDATION] Validation rÃ©ussie - toutes les images sont disponibles');
      console.log(`âœ… [VALIDATION] Total: ${blobImages.length} blob temporaires + ${persistentImages.length} persistantes`);
    } else {
      console.log(`âŒ [VALIDATION] Validation Ã©chouÃ©e - ${missingBlobs.length}/${blobImages.length} image(s) blob temporaire(s) manquante(s)`);
      console.log('âŒ [VALIDATION] Images manquantes dÃ©tail:', missingBlobs);
    }

    return {
      valid: isValid,
      missingBlobs: missingBlobs,
      totalImages: blobImages.length + persistentImages.length
    };
  };

  // Extraire et sauvegarder les images blob
  const extractAndSaveImages = async (htmlContent, documentDir) => {
    console.log('ðŸ” extractAndSaveImages appelÃ©e avec HTML:', htmlContent.substring(0, 200) + '...');

    // Parser le HTML avec DOMParser
    const parser = new DOMParser();
    const doc = parser.parseFromString(htmlContent, 'text/html');

    // Trouver toutes les images avec src blob:
    const images = doc.querySelectorAll('img[src^="blob:"]');

    console.log('ðŸ” Nombre d\'images trouvÃ©es:', images.length);
    images.forEach((img, i) => {
      console.log(`ðŸ” Image ${i + 1}:`, img.src, '|', img.alt);
    });

    if (images.length === 0) {
      console.log('âŒ Aucune image trouvÃ©e - HTML:', htmlContent);
      return htmlContent; // Pas d'images Ã  traiter
    }

    // CrÃ©er le dossier images
    const imagesDir = await documentDir.getDirectoryHandle('images', { create: true });

    // Traiter chaque image sÃ©parÃ©ment (pas de dÃ©duplication)
    for (const img of images) {
      const imageUrl = img.src;
      const altText = img.alt || 'image';

      try {
        let blob;

        // VÃ©rifier d'abord si on a le blob en mÃ©moire
        if (imageUrl.startsWith('blob:') && getBlobFromUrl) {
          const storedFile = getBlobFromUrl(imageUrl);
          if (storedFile) {
            console.log('âœ… Blob trouvÃ© en mÃ©moire pour:', imageUrl, 'Name:', storedFile?.name, 'Size:', storedFile?.size);
            blob = storedFile;
          } else {
            console.log('âš ï¸ Blob non trouvÃ© en mÃ©moire pour:', imageUrl);
            console.log('ðŸ“Š Blobs disponibles en mÃ©moire:', getAllBlobs ? getAllBlobs().size : 'getAllBlobs non disponible');
            if (getAllBlobs) {
              console.log('ðŸ” URLs des blobs stockÃ©s:', Array.from(getAllBlobs().keys()));
            }
            console.log('ðŸ”„ Tentative de fetch:', imageUrl);
            const response = await fetch(imageUrl);
            blob = await response.blob();
          }
        } else {
          const response = await fetch(imageUrl);
          blob = await response.blob();
        }

        if (!blob) {
          console.error('âŒ Impossible de rÃ©cupÃ©rer l\'image:', imageUrl);
          continue;
        }

        // DÃ©terminer l'extension du fichier
        const mimeType = blob.type || 'image/png';
        const extension = mimeType.split('/')[1] || 'png';

        // Utiliser le nom de l'image ou dÃ©faut
        const originalName = altText || 'image';
        const fileName = generateImageFileName(originalName, extension);

        // Sauvegarder l'image
        const imageHandle = await imagesDir.getFileHandle(fileName, { create: true });
        const writable = await imageHandle.createWritable();
        await writable.write(blob);
        await writable.close();

        // Mettre Ã  jour le src dans l'HTML pour pointer vers le fichier local
        img.src = `./images/${fileName}`;
        console.log('âœ… Image sauvegardÃ©e:', fileName);
      } catch (imageError) {
        console.error('âŒ Erreur sauvegarde image:', imageError);
      }
    }

    // Retourner le HTML modifiÃ© depuis le DOM
    return doc.body.innerHTML;
  };

  // Trouver un nom de dossier disponible (gestion des collisions)
  const findAvailableDirectoryName = async (parentDir, baseName) => {
    let finalName = baseName;
    let counter = 1;

    while (true) {
      try {
        // Tenter d'accÃ©der au dossier (si Ã§a marche, il existe dÃ©jÃ )
        await parentDir.getDirectoryHandle(finalName);
        // Le dossier existe, essayer le suivant
        finalName = `${baseName} (${counter})`;
        counter++;
      } catch (error) {
        // Le dossier n'existe pas, on peut l'utiliser
        break;
      }
    }

    return finalName;
  };

  // Fonction pour effectuer la sauvegarde
  const performSave = async (chosenDocumentName) => {
    try {
      setIsSaving(true);
      console.log('ðŸ’¾ [SAVE] DÃ©but du processus de sauvegarde...');

      // RÃ©cupÃ©rer le HTML actuel
      const htmlContent = editorRef.current?.innerHTML || content;
      console.log('ðŸ’¾ [SAVE] HTML rÃ©cupÃ©rÃ©, longueur:', htmlContent.length);

      // Ã‰TAPE 1: Validation prÃ©-sauvegarde des images
      console.log('ðŸ” [SAVE] Ã‰tape 1 - Validation prÃ©-sauvegarde...');
      const validationResult = validateImagesBeforeSave(htmlContent);

      if (!validationResult.valid) {
        console.log('âŒ [SAVE] Sauvegarde bloquÃ©e - images manquantes dÃ©tectÃ©es');
        const missingCount = validationResult.missingBlobs.length;
        const totalCount = validationResult.totalImages;

        let errorMessage = `âŒ Impossible de sauvegarder le document\n\n`;
        errorMessage += `${missingCount} image(s) sur ${totalCount} ne sont pas disponibles en mÃ©moire.\n\n`;
        errorMessage += `Images concernÃ©es :\n`;

        validationResult.missingBlobs.forEach(missing => {
          errorMessage += `â€¢ Image ${missing.index}: "${missing.alt}"\n`;
        });

        errorMessage += `\nVeuillez :\n`;
        errorMessage += `1. RÃ©insÃ©rer les images manquantes\n`;
        errorMessage += `2. Ou supprimer les images corrompues\n`;
        errorMessage += `3. Puis rÃ©essayer la sauvegarde`;

        alert(errorMessage);
        setIsSaving(false);
        return;
      }

      console.log('âœ… [SAVE] Validation rÃ©ussie - poursuite de la sauvegarde');

      // Ã‰TAPE 2: SÃ©lection du dossier de destination
      console.log('ðŸ’¾ [SAVE] Ã‰tape 2 - SÃ©lection du dossier de destination...');
      const parentDirectoryHandle = await window.showDirectoryPicker({
        mode: 'readwrite'
      });

      // Trouver un nom de dossier disponible (Ã©viter les collisions)
      const availableDocumentName = await findAvailableDirectoryName(
        parentDirectoryHandle, 
        chosenDocumentName.trim()
      );

      // CrÃ©er le dossier du document avec le nom disponible
      const documentDir = await parentDirectoryHandle.getDirectoryHandle(
        availableDocumentName, 
        { create: true }
      );

      // Ã‰TAPE 3: Extraire et sauvegarder les images (validation dÃ©jÃ  passÃ©e)
      console.log('ðŸ’¾ [SAVE] Ã‰tape 3 - Extraction et sauvegarde des images...');
      const updatedHtmlContent = await extractAndSaveImages(htmlContent, documentDir);

      // Convertir en markdown avec les nouveaux chemins d'images
      const markdownContent = htmlToMarkdown(updatedHtmlContent);

      // Sauvegarder le fichier markdown (utiliser le nom disponible)
      const fileName = `${availableDocumentName}.md`;
      const fileHandle = await documentDir.getFileHandle(fileName, { create: true });
      const writable = await fileHandle.createWritable();
      await writable.write(markdownContent);
      await writable.close();

      const imageCount = (updatedHtmlContent.match(/src=["']\.\/images\/[^"']+["']/g) || []).length;
