import React, { useRef, useEffect, useState, useCallback } from 'react';
import { useEditor } from './hooks/useEditor';
import { useEelBridge } from './hooks/useEelBridge';
import { useThemeSettings } from './hooks/useThemeSettings';
import { useMathJax } from './hooks/useMathJax';
import { htmlToMarkdown, markdownToHtml } from './utils/markdownConverter';
// Removed PromptoDYS imports - using simple project management
import Toolbar from './components/Toolbar';
import Editor from './components/Editor';
import StatusBar from './components/StatusBar';
import ThemeSettings from './components/ThemeSettings';

const App = () => {

  const {
    content,
    setContent,
    viewMode,
    setViewMode,
    changeViewMode,
    isWysiwyg,
    currentFormat,
    setCurrentFormat,
    editorRef,
    handleInput,
    updateCurrentFormat,
    ignoreSelectionChangeRef,
    selectedImage,
    setSelectedImage,
    handleImageClick,
    handleEditorClick
  } = useEditor();

  // Sauvegarde automatique pour refresh F5
  useEffect(() => {
    const saveForRefresh = () => {
      const refreshData = {
        content: content,
        viewMode: viewMode,
        timestamp: Date.now()
      };
      localStorage.setItem('editor-refresh-backup', JSON.stringify(refreshData));
    };

    // Sauvegarder à chaque changement de contenu
    if (content) {
      saveForRefresh();
    }

    // Nettoyer avant de quitter
    const handleBeforeUnload = () => {
      saveForRefresh();
    };

    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, [content, viewMode]);

  // Restaurer après refresh F5 (une seule fois au montage)
  useEffect(() => {
    const restoreAfterRefresh = () => {
      try {
        const refreshData = localStorage.getItem('editor-refresh-backup');
        if (refreshData) {
          const parsed = JSON.parse(refreshData);
          const timeDiff = Date.now() - parsed.timestamp;

          // Restaurer seulement si c'est un refresh récent (< 5 secondes)
          if (timeDiff < 5000) {
            if (parsed.content) {
              setContent(parsed.content);
            }
            if (parsed.viewMode) {
              setViewMode(parsed.viewMode);
            }
          }

          // Supprimer la sauvegarde après restauration
          localStorage.removeItem('editor-refresh-backup');
        }
      } catch (error) {
        console.error('Erreur lors de la restauration après refresh:', error);
      }
    };

    restoreAfterRefresh();
  }, []); // Exécuter une seule fois au montage

  // Hook pour paramètres de thème
  const { 
    settings: themeSettings, 
    isSettingsOpen: isThemeSettingsOpen, 
    toggleSettings: toggleThemeSettings,
    backgroundOptions,
    textOptions,
    fontOptions,
    updateSetting,
    resetToDefaults
  } = useThemeSettings();

  // ⚡ Intégration MathJax en local
  const { renderMath } = useMathJax();

  // État de restauration supprimé - éditeur volatil uniquement

  // ⚡ Intégration Eel pour support desktop
  useEelBridge(content, setContent, viewMode);

  return (
      <div className="h-screen bg-white w-full relative">
        {/* Removed PromptoDYS error messages */}


        {/* Zone d'édition principale avec scroll indépendant */}
        <div className="editor-container">
          <div className="editor-scroll">
            <Editor 
              viewMode={viewMode}
              content={content}
              editorRef={editorRef}
              onInput={handleInput}
              onSelectionChange={updateCurrentFormat}
              currentFormat={currentFormat}
              selectedImage={selectedImage}
              onImageClick={handleImageClick}
              onEditorClick={handleEditorClick}
              onDeleteSelectedImage={null}
              ignoreSelectionChangeRef={ignoreSelectionChangeRef}
            />
          </div>
        </div>

        {/* Toolbar fixée en bas */}
        <div className="fixed bottom-8 left-0 right-0 bg-gray-50 border-t border-gray-200 px-5 py-2 z-10">
          <Toolbar 
            content={content}
            setContent={setContent}
            viewMode={viewMode}
            onViewModeChange={changeViewMode}
            currentFormat={currentFormat}
            onFormatChange={setCurrentFormat}
            editorRef={editorRef}
            onThemeSettingsToggle={toggleThemeSettings}
            ignoreSelectionChangeRef={ignoreSelectionChangeRef}
          />
        </div>

        {/* StatusBar tout en bas */}
        <div className="fixed bottom-0 left-0 right-0 bg-gray-100 border-t border-gray-300 px-5 py-1 z-10">
          <StatusBar 
            currentFormat={currentFormat}
            content={content}
            dysSettings={themeSettings}
          />
        </div>

        {/* Modal ThemeSettings */}
        <ThemeSettings 
          settings={themeSettings}
          isSettingsOpen={isThemeSettingsOpen}
          toggleSettings={toggleThemeSettings}
          backgroundOptions={backgroundOptions}
          textOptions={textOptions}
          fontOptions={fontOptions}
          updateSetting={updateSetting}
          resetToDefaults={resetToDefaults}
        />
      </div>
    );
};

export default App;
