import React from 'react';

const ThemeSettings = ({
  settings,
  backgroundOptions,
  textOptions,
  fontOptions,
  isSettingsOpen,
  updateSetting,
  resetToDefaults,
  toggleSettings,
  isValidCombination
}) => {

  if (!isSettingsOpen) return null;

  const styles = {
    floatingPanel: {
      position: 'fixed',
      top: '20px',
      right: '20px',
      width: '400px',
      minWidth: '350px',
      maxWidth: '600px',
      minHeight: '500px',
      maxHeight: '80vh',
      resize: 'both',
      overflow: 'auto',
      zIndex: 1000,
      cursor: 'default'
    },
    modal: {
      backgroundColor: 'white',
      padding: '20px',
      borderRadius: '12px',
      boxShadow: '0 10px 25px rgba(0, 0, 0, 0.3)',
      height: '100%',
      width: '100%',
      overflowY: 'auto',
      border: '2px solid #e5e7eb'
    },
    dragHandle: {
      backgroundColor: '#f3f4f6',
      padding: '10px',
      borderRadius: '8px 8px 0 0',
      borderBottom: '1px solid #e5e7eb',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      fontSize: '14px',
      fontWeight: '600',
      color: '#374151'
    },
    panel: {
      backgroundColor: 'white',
      borderRadius: '8px',
      padding: '24px',
      maxWidth: '500px',
      width: '90%',
      maxHeight: '80vh',
      overflowY: 'auto',
      boxShadow: '0 10px 25px rgba(0, 0, 0, 0.1)'
    },
    header: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '24px',
      borderBottom: '1px solid #e5e7eb',
      paddingBottom: '12px'
    },
    title: {
      fontSize: '18px',
      fontWeight: '600',
      color: '#1f2937',
      margin: 0
    },
    closeButton: {
      backgroundColor: '#f3f4f6',
      border: '1px solid #d1d5db',
      borderRadius: '6px',
      cursor: 'pointer',
      padding: '8px 12px',
      display: 'flex',
      alignItems: 'center',
      gap: '6px',
      transition: 'all 0.2s ease',
      position: 'relative',
      fontSize: '14px',
      fontWeight: '500',
      color: '#374151'
    },
    section: {
      marginBottom: '20px'
    },
    sectionTitle: {
      fontSize: '14px',
      fontWeight: '600',
      color: '#374151',
      marginBottom: '8px'
    },
    optionGroup: {
      display: 'flex',
      gap: '8px',
      flexWrap: 'wrap'
    },
    colorButton: {
      width: '40px',
      height: '40px',
      borderRadius: '8px',
      border: '2px solid transparent',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '12px',
      fontWeight: '500'
    },
    activeColorButton: {
      borderColor: '#3b82f6',
      boxShadow: '0 0 0 2px rgba(59, 130, 246, 0.2)'
    },
    disabledColorButton: {
      opacity: '0.3',
      cursor: 'not-allowed',
      filter: 'grayscale(50%)'
    },
    select: {
      width: '100%',
      padding: '8px 12px',
      border: '1px solid #d1d5db',
      borderRadius: '6px',
      fontSize: '14px',
      backgroundColor: 'white'
    },
    sliderContainer: {
      display: 'flex',
      alignItems: 'center',
      gap: '12px'
    },
    slider: {
      flex: 1,
      height: '6px',
      borderRadius: '3px',
      background: '#e5e7eb',
      outline: 'none',
      cursor: 'pointer'
    },
    sliderValue: {
      fontSize: '14px',
      fontWeight: '500',
      color: '#374151',
      minWidth: '40px',
      textAlign: 'center'
    },
  };

  return (
    <div style={styles.floatingPanel}>
      <div style={styles.modal}>
        <div style={styles.dragHandle}>
          <span>⚙️ Paramètres du thème</span>
          <button 
            onClick={toggleSettings}
            style={styles.closeButton}
            title="Fermer les paramètres du thème"
          >
            ❌ Fermer
          </button>
        </div>
        {/* Section Couleur de fond */}
        <div style={styles.section}>
          <div style={styles.sectionTitle}>Couleur d'arrière-plan</div>
          <div style={styles.optionGroup}>
            {backgroundOptions.map(option => {
              const isDisabled = !isValidCombination(option.value, settings.textColor);
              return (
                <button
                  key={option.value}
                  style={{
                    ...styles.colorButton,
                    backgroundColor: option.value,
                    color: (option.value === '#fafafa' || option.value === '#fffef7' || option.value === '#f0f7ff' || option.value === '#f0fdf4' || option.value === '#fefbf3') ? '#374151' : '#f7fafc',
                    ...(settings.backgroundColor === option.value ? styles.activeColorButton : {}),
                    ...(isDisabled ? styles.disabledColorButton : {})
                  }}
                  onClick={() => !isDisabled && updateSetting('backgroundColor', option.value)}
                  title={isDisabled ? `${option.name} - Incompatible avec le texte actuel` : option.name}
                  disabled={isDisabled}
                >
                  {option.name.charAt(0)}
                </button>
              );
            })}
          </div>
        </div>

        {/* Section Couleur de texte */}
        <div style={styles.section}>
          <div style={styles.sectionTitle}>Couleur du texte</div>
          <div style={styles.optionGroup}>
            {textOptions.map(option => {
              const isDisabled = !isValidCombination(settings.backgroundColor, option.value);
              return (
                <button
                  key={option.value}
                  style={{
                    ...styles.colorButton,
                    backgroundColor: option.value,
                    color: option.value === '#f7fafc' || option.value === '#e2e8f0' || option.value === '#faf5f0' ? '#374151' : 'white',
                    ...(settings.textColor === option.value ? styles.activeColorButton : {}),
                    ...(isDisabled ? styles.disabledColorButton : {})
                  }}
                  onClick={() => !isDisabled && updateSetting('textColor', option.value)}
                  title={isDisabled ? `${option.name} - Incompatible avec le fond actuel` : option.name}
                  disabled={isDisabled}
                >
                  Aa
                </button>
              );
            })}
          </div>
        </div>

        {/* Section Police */}
        <div style={styles.section}>
          <div style={styles.sectionTitle}>Police de caractères</div>
          <select
            style={styles.select}
            value={settings.fontFamily}
            onChange={e => updateSetting('fontFamily', e.target.value)}
          >
            {fontOptions.map(option => (
              <option key={option.value} value={option.value}>
                {option.name}
              </option>
            ))}
          </select>
        </div>

        {/* Section Taille de police */}
        <div style={styles.section}>
          <div style={styles.sectionTitle}>Taille de police</div>
          <div style={styles.sliderContainer}>
            <input
              type="range"
              min="12"
              max="24"
              step="1"
              value={settings.fontSize}
              onChange={e => updateSetting('fontSize', parseInt(e.target.value))}
              style={styles.slider}
            />
            <div style={styles.sliderValue}>{settings.fontSize}px</div>
          </div>
        </div>

        {/* Section Interlignage */}
        <div style={styles.section}>
          <div style={styles.sectionTitle}>Espacement des lignes</div>
          <div style={styles.sliderContainer}>
            <input
              type="range"
              min="1.2"
              max="2.0"
              step="0.1"
              value={settings.lineHeight}
              onChange={e => updateSetting('lineHeight', parseFloat(e.target.value))}
              style={styles.slider}
            />
            <div style={styles.sliderValue}>{settings.lineHeight.toFixed(1)}</div>
          </div>
        </div>

      </div>
    </div>
  );
};

export default ThemeSettings;
