import React, { useCallback, useEffect, useState } from 'react';
import { detectBase64, applyBase64Collapse } from '../utils/base64Collapse';
import { convertAllPromptoDysUrlsToBlobs, getCurrentProject } from '../utils/promptoDysManager';
import { useImageStorage } from '../hooks/useImageStorage';

const Editor = ({ 
  viewMode,  
  content, 
  editorRef, 
  onInput, 
  onSelectionChange,
  currentFormat
}) => {

  // √âtat pour g√©rer l'expansion des donn√©es Base64
  const [expandedBase64, setExpandedBase64] = useState({});

  // Hook pour g√©rer les images IndexedDB
  const { loadImage } = useImageStorage();

  // Gestionnaire pour l'auto-redimensionnement (toujours d√©clar√©)
  const handleTextareaResize = useCallback((textarea) => {
    if (textarea) {
      textarea.style.height = 'auto';
      textarea.style.height = Math.max(384, textarea.scrollHeight) + 'px';
    }
  }, []);

  // Gestionnaire pour toggle Base64
  const handleBase64Toggle = useCallback((e) => {
    const clickedText = e.target.value;
    const cursorPos = e.target.selectionStart;

    // Chercher si on a cliqu√© sur un bouton "üëÅÔ∏è Voir"
    const beforeCursor = clickedText.substring(Math.max(0, cursorPos - 100), cursorPos + 20);
    const toggleMatch = beforeCursor.match(/üëÅÔ∏è Voir/);

    if (toggleMatch) {
      // Trouver l'index du Base64 correspondant
      const base64Matches = detectBase64(content);

      // Pour simplifier, on toggle le premier Base64 trouv√©
      // Dans une version plus avanc√©e, on pourrait calculer l'index exact
      if (base64Matches.length > 0) {
        const toggleIndex = 0; // Simplifi√© pour cette version
        setExpandedBase64(prev => ({
          ...prev,
          [toggleIndex]: !prev[toggleIndex]
        }));
      }
    }
  }, [content]);

  // Fonction pour obtenir le contenu avec Base64 collaps√©s
  const getCollapsedContent = useCallback((originalContent) => {
    if (viewMode === 'wysiwyg') {
      return originalContent; // Pas de collapse en WYSIWYG
    }
    return applyBase64Collapse(originalContent, expandedBase64);
  }, [viewMode, expandedBase64]);

  // Fonction pour formater le HTML avec des retours √† la ligne et indentation
  const formatHtmlForSource = useCallback((html) => {
    let indentLevel = 0;
    const indentSize = 2; // 2 espaces par niveau

    return html
      .replace(/<br\s*\/?>/gi, '<br>\n')
      .replace(/<\/?(h[1-6]|p|div|ul|ol|li|strong|em|span)[^>]*>/gi, (match) => {
        if (match.startsWith('</')) {
          // Balise fermante : diminuer l'indentation puis ajouter la balise
          indentLevel = Math.max(0, indentLevel - 1);
          return match + '\n';
        } else {
          // Balise ouvrante : ajouter la balise puis augmenter l'indentation
          const result = '\n' + ' '.repeat(indentLevel * indentSize) + match;
          // Augmenter l'indentation pour les balises conteneurs
          if (match.match(/<(ul|ol|li|div|p|h[1-6])[^>]*>/i)) {
            indentLevel++;
          }
          return result;
        }
      })
      .split('\n')
      .map((line, index) => {
        if (index === 0) return line.trim(); // Premi√®re ligne sans indentation
        if (line.trim() === '') return ''; // Ligne vide
        if (line.trim().startsWith('</')) {
          // Balise fermante : r√©duire l'indentation
          const currentIndent = Math.max(0, indentLevel - 1);
          return ' '.repeat(currentIndent * indentSize) + line.trim();
        }
        // Contenu texte : utiliser l'indentation actuelle
        return line.startsWith(' ') ? line : ' '.repeat(indentLevel * indentSize) + line.trim();
      })
      .join('\n')
      .replace(/^\n+/, '') // Supprimer les retours √† la ligne en d√©but
      .replace(/\n{2,}/g, '\n') // Supprimer toutes les lignes vides multiples
      .trim();
  }, []);

  // Gestionnaire unifi√© pour tous les changements
  const handleChange = useCallback((e) => {
    if (viewMode === 'wysiwyg' && editorRef.current) {
      // Nettoyer les spans vides et les &nbsp; en trop
      const cleanContent = (html) => {
        return html
          .replace(/<span(?![^>]*style="[^"]*color:)[^>]*>\s*<\/span>/g, '') // Supprimer spans vides SAUF les spans color√©s
          .replace(/(&nbsp;\s*){2,}/g, '&nbsp;') // R√©duire les &nbsp; multiples
          .replace(/<font[^>]*color="([^"]*)"[^>]*>(.*?)<\/font>/g, '<span style="color: $1;">$2</span>') // Convertir font en span
          .replace(/<b\b[^>]*>(.*?)<\/b>/g, '<strong>$1</strong>') // Convertir b en strong
          .replace(/<i\b[^>]*>(.*?)<\/i>/g, '<em>$1</em>'); // Convertir i en em
      };

      const newContent = cleanContent(editorRef.current.innerHTML);

      // √âviter les boucles infinies avec une v√©rification plus stricte
      if (newContent !== content && newContent.trim() !== content.trim()) {
        onInput({ target: { innerHTML: newContent, value: newContent } });
      }

      // Re-rendre MathJax apr√®s modification du contenu
      if (window.MathJax && window.MathJax.typesetPromise) {
        window.MathJax.typesetPromise([editorRef.current]).catch((err) => {
          console.warn('Erreur MathJax:', err);
        });
      }

      // Mettre √† jour l'√©tat de formatage avec un d√©lai pour √©viter les conflits
      requestAnimationFrame(() => {
        if (document.activeElement === editorRef.current) {
          onSelectionChange();
        }
      });
    }
  }, [viewMode, editorRef, onInput, onSelectionChange, content]);

  // Gestionnaire pour les √©v√©nements de s√©lection/curseur
  const handleSelectionChange = useCallback(() => {
    if (viewMode === 'wysiwyg' && document.activeElement === editorRef.current) {
      onSelectionChange();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [viewMode, onSelectionChange]);

  // Fonction pour afficher toutes les images IndexedDB via CSS
  const displayAllIndexedImages = useCallback(async () => {
    if (!editorRef.current) return;

    const images = editorRef.current.querySelectorAll('img[src^="indexed://"]');

    for (const img of images) {
      const imageId = img.src.replace('indexed://', '');
      console.log('üîç Affichage image IndexedDB:', imageId);

      try {
        const imageData = await loadImage(imageId);
        if (imageData && imageData.blobUrl) {
          // Afficher via CSS (pr√©serve indexed:// dans src)
          img.style.backgroundImage = `url(${imageData.blobUrl})`;
          img.style.backgroundSize = 'contain';
          img.style.backgroundRepeat = 'no-repeat';
          img.style.backgroundPosition = 'center';
          img.style.minHeight = '100px';
          // Masquer le texte de fallback
          img.style.color = 'transparent';
          img.style.fontSize = '0';
          console.log('‚úÖ Image affich√©e via CSS:', imageId, '‚Üí', imageData.blobUrl);
        }
      } catch (error) {
        console.warn('‚ö†Ô∏è Erreur affichage image:', imageId, error);
      }
    }
  }, [loadImage]);


  // Initialiser le contenu uniquement si n√©cessaire
  useEffect(() => {
    const initializeContent = async () => {
      if (viewMode === 'wysiwyg' && editorRef.current && editorRef.current.innerHTML !== content) {
        // Ne pas modifier le contenu si l'utilisateur interagit activement
        const hasFocus = document.activeElement === editorRef.current;
        const hasSelection = window.getSelection().rangeCount > 0;

        // √âviter les mises √† jour pendant l'interaction utilisateur
        if (hasFocus && hasSelection) {
          return; // Ne pas perturber l'utilisateur
        }

        // Convertir les URLs relatives PromptoDYS en Blob URLs pour affichage
        const currentProject = getCurrentProject();
        let displayContent = content;

        if (currentProject.directory) {
          displayContent = await convertAllPromptoDysUrlsToBlobs(content, currentProject.directory);
        }

        // Mise √† jour avec le contenu converti (IndexedDB g√©r√© par MutationObserver)
        editorRef.current.innerHTML = displayContent;

        // Re-rendre MathJax apr√®s mise √† jour du contenu
        if (window.MathJax && window.MathJax.typesetPromise) {
          window.MathJax.typesetPromise([editorRef.current]).catch((err) => {
            console.warn('Erreur MathJax:', err);
          });
        }
      }
    };

    initializeContent();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [viewMode, content]);

  // Gestionnaire pour la copie - convertir HTML en texte propre
  const handleCopy = useCallback((e) => {
    if (viewMode === 'wysiwyg' && editorRef.current) {
      const selection = window.getSelection();
      if (selection.rangeCount > 0) {
        const range = selection.getRangeAt(0);
        const selectedContent = range.cloneContents();
        const tempDiv = document.createElement('div');
        tempDiv.appendChild(selectedContent);

        // Convertir HTML en texte avec retours √† la ligne pr√©serv√©s
        const htmlContent = tempDiv.innerHTML;
        const textContent = htmlContent
          .replace(/<div[^>]*>/gi, '')
          .replace(/<\/div>/gi, '\n')
          .replace(/<br\s*\/?>/gi, '\n')
          .replace(/<p[^>]*>/gi, '')
          .replace(/<\/p>/gi, '\n')
          .replace(/<[^>]+>/g, '') // Supprimer toutes les autres balises
          .replace(/&nbsp;/g, ' ')
          .replace(/&amp;/g, '&')
          .replace(/&lt;/g, '<')
          .replace(/&gt;/g, '>')
          .replace(/&quot;/g, '"')
          .replace(/&#39;/g, "'")
          .replace(/\n+$/, ''); // Supprimer les retours √† la ligne en fin

        // Mettre le texte propre dans le presse-papier
        e.clipboardData.setData('text/plain', textContent);
        e.preventDefault();
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [viewMode]);

  // √âcouter les changements de s√©lection et la copie
  useEffect(() => {
    if (viewMode === 'wysiwyg') {
      document.addEventListener('selectionchange', handleSelectionChange);
      editorRef.current?.addEventListener('copy', handleCopy);

      return () => {
        document.removeEventListener('selectionchange', handleSelectionChange);
        editorRef.current?.removeEventListener('copy', handleCopy);
      };
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [viewMode, handleSelectionChange, handleCopy]);

  // Effect pour affichage des images IndexedDB (Option 1: pr√©servation DOM)
  useEffect(() => {
    if (viewMode === 'wysiwyg' && editorRef.current) {
      // Affichage initial des images d√©j√† pr√©sentes
      displayAllIndexedImages();

      const observer = new MutationObserver((mutations) => {
        mutations.forEach(async (mutation) => {
          // V√©rifier les nouveaux n≈ìuds ajout√©s
          mutation.addedNodes.forEach(async (node) => {
            if (node.nodeType === Node.ELEMENT_NODE) {
              // Chercher les images avec src indexed://
              const images = node.tagName === 'IMG' 
                ? [node] 
                : node.querySelectorAll ? node.querySelectorAll('img[src^="indexed://"]') : [];

              for (const img of images) {
                if (img.src && img.src.startsWith('indexed://')) {
                  const imageId = img.src.replace('indexed://', '');
                  console.log('üîç Image IndexedDB d√©tect√©e (mutation):', imageId);

                  try {
                    const imageData = await loadImage(imageId);
                    if (imageData && imageData.blobUrl) {
                      // Afficher via CSS (pr√©serve indexed:// dans src)
                      img.style.backgroundImage = `url(${imageData.blobUrl})`;
                      img.style.backgroundSize = 'contain';
                      img.style.backgroundRepeat = 'no-repeat';
                      img.style.backgroundPosition = 'center';
                      img.style.minHeight = '100px';
                      console.log('‚úÖ Image affich√©e via CSS (mutation):', imageId, '‚Üí', imageData.blobUrl);
                    }
                  } catch (error) {
                    console.warn('‚ö†Ô∏è Erreur affichage image (mutation):', imageId, error);
                  }
                }
              }
            }
          });

          // V√©rifier les modifications d'attributs (src modifi√©)
          if (mutation.type === 'attributes' && mutation.attributeName === 'src') {
            const img = mutation.target;
            if (img.tagName === 'IMG' && img.src && img.src.startsWith('indexed://')) {
              const imageId = img.src.replace('indexed://', '');
              console.log('üîç Attribut src modifi√© vers IndexedDB:', imageId);

              try {
                const imageData = await loadImage(imageId);
                if (imageData && imageData.blobUrl) {
                  // Afficher via CSS (pr√©serve indexed:// dans src)
                  img.style.backgroundImage = `url(${imageData.blobUrl})`;
                  img.style.backgroundSize = 'contain';
                  img.style.backgroundRepeat = 'no-repeat';
                  img.style.backgroundPosition = 'center';
                  img.style.minHeight = '100px';
                  console.log('‚úÖ Image affich√©e via CSS (attr):', imageId, '‚Üí', imageData.blobUrl);
                }
              } catch (error) {
                console.warn('‚ö†Ô∏è Erreur affichage image (attr):', imageId, error);
              }
            }
          }
        });
      });

      // Observer les modifications dans l'√©diteur
      observer.observe(editorRef.current, {
        childList: true,
        subtree: true,
        attributes: true,
        attributeFilter: ['src']
      });

      return () => {
        observer.disconnect();
      };
    }
  }, [viewMode, loadImage, displayAllIndexedImages]);

// Effect pour ajouter les poign√©es de redimensionnement aux images
useEffect(() => {
  if (viewMode === 'wysiwyg' && editorRef.current) {
    const addResizeHandlesToImages = () => {
      const images = editorRef.current.querySelectorAll('img:not([data-resizable])');

        images.forEach((img) => {
          img.setAttribute('data-resizable', 'true');

          // Wrapper l'image dans un conteneur redimensionnable
          const wrapper = document.createElement('div');
          wrapper.className = 'resizable-image';
          wrapper.style.width = img.offsetWidth + 'px';
          wrapper.style.height = img.offsetHeight + 'px';

          // Ins√©rer le wrapper
          img.parentNode.insertBefore(wrapper, img);
          wrapper.appendChild(img);

          // Cr√©er les 4 poign√©es
          ['nw', 'ne', 'sw', 'se'].forEach((corner) => {
            const handle = document.createElement('div');
            handle.className = `resize-handle ${corner}`;
            handle.dataset.corner = corner;

            handle.addEventListener('mousedown', (e) => {
              e.preventDefault();
              e.stopPropagation();

              const startX = e.clientX;
              const startY = e.clientY;
              const startWidth = wrapper.offsetWidth;
              const startHeight = wrapper.offsetHeight;
              const aspectRatio = startWidth / startHeight;

              const handleMouseMove = (e) => {
                const deltaX = e.clientX - startX;
                const deltaY = e.clientY - startY;

                let newWidth = startWidth;
                let newHeight = startHeight;

                // Calculer les nouvelles dimensions selon le coin
                switch(corner) {
                  case 'se': // Sud-Est
                    newWidth = Math.max(50, startWidth + deltaX);
                    newHeight = newWidth / aspectRatio;
                    break;
                  case 'sw': // Sud-Ouest
                    newWidth = Math.max(50, startWidth - deltaX);
                    newHeight = newWidth / aspectRatio;
                    break;
                  case 'ne': // Nord-Est
                    newWidth = Math.max(50, startWidth + deltaX);
                    newHeight = newWidth / aspectRatio;
                    break;
                  case 'nw': // Nord-Ouest
                    newWidth = Math.max(50, startWidth - deltaX);
                    newHeight = newWidth / aspectRatio;
                    break;
                }

                // Appliquer les nouvelles dimensions
                wrapper.style.width = newWidth + 'px';
                wrapper.style.height = newHeight + 'px';
                img.style.width = '100%';
                img.style.height = '100%';

                // Ajouter les attributs HTML width/height pour persistance lors des conversions
                img.setAttribute('width', Math.round(newWidth) + 'px');
                img.setAttribute('height', Math.round(newHeight) + 'px');
              };

              const handleMouseUp = () => {
                document.removeEventListener('mousemove', handleMouseMove);
                document.removeEventListener('mouseup', handleMouseUp);

                // D√©clencher l'√©v√©nement de changement pour sauvegarder
                if (onInput) {
                  const event = new Event('input', { bubbles: true });
                  editorRef.current.dispatchEvent(event);
                }
              };

              document.addEventListener('mousemove', handleMouseMove);
              document.addEventListener('mouseup', handleMouseUp);
            });

            wrapper.appendChild(handle);
          });
        });
      };

      // Observer les mutations DOM pour les nouvelles images
      const observer = new MutationObserver(() => {
        setTimeout(addResizeHandlesToImages, 100);
      });

      observer.observe(editorRef.current, {
        childList: true,
        subtree: true
      });

      // Ajouter les poign√©es aux images existantes
      setTimeout(addResizeHandlesToImages, 100);

      return () => observer.disconnect();
    }
  }, [viewMode, content, onInput]);

  // Rendu conditionnel avec switch/case pour garantir une seule vue
  console.log('üîÑ Editor.js - Rendu switch/case, viewMode:', viewMode);

  switch (viewMode) {
    case 'wysiwyg':
      console.log('üéØ SWITCH - Vue WYSIWYG');
      return (
        <div key="wysiwyg">
          <style>{`
            .editor-content h1 { 
              font-size: 2em; 
              font-weight: bold; 
              margin: 0.67em 0; 
              line-height: 1.2;
            }
            .editor-content h2 { 
              font-size: 1.5em; 
              font-weight: bold; 
              margin: 0.75em 0; 
              line-height: 1.3;
            }
            .editor-content h3 { 
              font-size: 1.17em; 
              font-weight: bold; 
              margin: 0.83em 0; 
              line-height: 1.4;
            }
            .editor-content p {
              margin: 0.5em 0;
            }
            .editor-content ul {
              margin: 0.5em 0;
              padding-left: 2em;
              list-style-type: disc;
            }
            .editor-content ol {
              margin: 0.5em 0;
              padding-left: 2em;
              list-style-type: decimal;
            }
            .editor-content ol[style*="lower-alpha"] {
              list-style-type: lower-alpha;
            }
            .editor-content li {
              margin: 0.25em 0;
            }

            /* Styles pour images redimensionnables */
            .editor-content img {
              max-width: 100%;
              height: auto;
              position: relative;
              cursor: pointer;
            }

            .resizable-image {
              position: relative;
              display: inline-block;
              border: 2px solid transparent;
            }

            .resizable-image:hover {
              border: 2px solid #3b82f6;
            }

            .resizable-image .resize-handle {
              position: absolute;
              width: 10px;
              height: 10px;
              background: #3b82f6;
              border: 2px solid white;
              border-radius: 50%;
              opacity: 0;
              cursor: nw-resize;
              transition: opacity 0.2s;
            }

            .resizable-image:hover .resize-handle {
              opacity: 1;
            }

            .resize-handle.nw { top: -6px; left: -6px; cursor: nw-resize; }
            .resize-handle.ne { top: -6px; right: -6px; cursor: ne-resize; }
            .resize-handle.sw { bottom: -6px; left: -6px; cursor: sw-resize; }
            .resize-handle.se { bottom: -6px; right: -6px; cursor: se-resize; }
          `}</style>
          <div
            ref={editorRef}
            contentEditable
            spellCheck={false}
            onInput={handleChange}
            suppressContentEditableWarning={true}
            className="editor-content p-2 focus:outline-none h-full"
            style={{ 
              fontFamily: 'var(--dys-font-family)',
              fontSize: 'var(--dys-font-size)',
              lineHeight: 'var(--dys-line-height)',
              color: 'var(--dys-text-color)',
              backgroundColor: 'var(--dys-bg-color)',
              overflow: 'auto',
              wordWrap: 'break-word',
              whiteSpace: 'pre-wrap',
              border: '1px solid #e5e7eb',
              paddingBottom: '2rem'
            }}
          />
        </div>
      );

    case 'markdown': {
      console.log('üìù SWITCH - Vue MARKDOWN');
      const displayContent = getCollapsedContent(content);

      return (
        <div key="markdown" className="relative">
          <textarea
            ref={(el) => {
              editorRef.current = el;
            }}
            value={displayContent}
            onChange={(e) => {
              const newValue = e.target.value;
              const originalWithCollapse = getCollapsedContent(content);

              if (newValue !== originalWithCollapse) {
                onInput({ ...e, target: { ...e.target, value: newValue } });
              }
            }}
            onClick={handleBase64Toggle}
            className="editor-content p-2 w-full h-full border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
            style={{
              fontFamily: 'var(--dys-font-family)',
              fontSize: 'var(--dys-font-size)',
              lineHeight: 'var(--dys-line-height)',
              color: 'var(--dys-text-color)',
              backgroundColor: 'var(--dys-bg-color)',
              overflow: 'auto',
              wordWrap: 'break-word',
              whiteSpace: 'pre-wrap',
              paddingBottom: '2rem'
            }}
            placeholder="√âcrivez en Markdown...

# Titre 1
## Titre 2
### Titre 3

**Gras** *Italique*

- Liste √† puces
1. Liste num√©rot√©e
a. Liste alphab√©tique

$E = mc^2$ (formule inline)
$$\\int_{-\\infty}^{\\infty} e^{-x^2} dx = \\sqrt{\\pi}$$ (formule block)"
          />
          {detectBase64(content).length > 0 && (
            <div className="absolute top-2 right-2 bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs">
              üì∑ {detectBase64(content).length} image(s) Base64
            </div>
          )}
        </div>
      );
    }

    case 'html': {
      console.log('üîß SWITCH - Vue HTML');
      const htmlDisplayContent = formatHtmlForSource(getCollapsedContent(content));

      return (
        <div key="html" className="relative">
          <textarea
            ref={(el) => {
              editorRef.current = el;
            }}
            value={htmlDisplayContent}
            onChange={(e) => {
              const newValue = e.target.value;
              const originalWithCollapse = formatHtmlForSource(getCollapsedContent(content));

              if (newValue !== originalWithCollapse) {
                onInput({ ...e, target: { ...e.target, value: newValue } });
              }
            }}
            onClick={handleBase64Toggle}
            className="w-full h-full p-2 focus:outline-none resize-none"
            style={{ 
              fontFamily: 'Monaco, "Cascadia Code", "Roboto Mono", monospace',
              overflow: 'auto',
              wordWrap: 'break-word',
              whiteSpace: 'pre-wrap',
              border: '1px solid #e5e7eb',
              paddingBottom: '2rem'
            }}
            placeholder="Code source HTML..."
          />
          {detectBase64(content).length > 0 && (
            <div className="absolute top-2 right-2 bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs">
              üì∑ {detectBase64(content).length} image(s) Base64
            </div>
          )}
        </div>
      );
    }

    default:
      console.log('‚ùå SWITCH - AUCUNE VUE CORRESPONDANTE, viewMode:', viewMode);
      return null;
  }
};

export default Editor;
