import React, { useContext } from 'react';
import EditorContext from '../contexts/EditorContext';
import { markdownToHtml, htmlToMarkdown } from '../utils/markdownConverter';
import { useImageStorage } from '../hooks/useImageStorage';
// Removed PromptoDYS imports - using simplified project management

const Toolbar = () => {
  const {
    content,
    editorRef,
    currentFormat,
    onFormatChange,
    onViewModeChange,
    viewMode,
    changeViewMode,
    onThemeSettingsToggle,
    onContentChange,
    ignoreSelectionChangeRef
  } = useContext(EditorContext);

  // Hook pour g√©rer le stockage d'images dans IndexedDB
  const { storeImage, convertBlobUrlsToIndexed } = useImageStorage();

  // V√©rifier le support File System Access API
  const isFileSystemAccessSupported = () => {
    return 'showDirectoryPicker' in window && 'showOpenFilePicker' in window;
  };

  // Solution simple: utiliser localStorage au lieu d'IndexedDB pour √©viter les conflits
  const PROMPTODYS_PATH_KEY = 'promptodys_directory_path';

  const storePromptoDYSPath = (directoryName) => {
    try {
      if (directoryName && directoryName.toLowerCase().includes('prompt')) {
        localStorage.setItem(PROMPTODYS_PATH_KEY, directoryName);
      }
    } catch (error) {
      console.warn('Erreur stockage PromptoDYS path:', error);
    }
  };

  const hasPromptoDYSPreference = () => {
    try {
      const storedPath = localStorage.getItem(PROMPTODYS_PATH_KEY);
      return storedPath !== null;
    } catch (error) {
      return false;
    }
  };

  // G√©n√©rer un nom de projet avec timestamp
  const generateDefaultProjectName = () => {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    const day = String(now.getDate()).padStart(2, '0');
    const hour = String(now.getHours()).padStart(2, '0');
    const minute = String(now.getMinutes()).padStart(2, '0');
    return `Projet_${year}${month}${day}_${hour}${minute}`;
  };

  // Remplacer prompt() par un input HTML temporaire
  const showProjectNameInput = () => {
    return new Promise((resolve) => {
      const defaultName = generateDefaultProjectName();

      // Cr√©er l'overlay
      const overlay = document.createElement('div');
      overlay.style.cssText = `
        position: fixed; top: 0; left: 0; width: 100%; height: 100%;
        background: rgba(0,0,0,0.5); display: flex; align-items: center;
        justify-content: center; z-index: 10000;
      `;

      // Cr√©er le dialog
      const dialog = document.createElement('div');
      dialog.style.cssText = `
        background: white; padding: 24px; border-radius: 8px;
        box-shadow: 0 10px 25px rgba(0,0,0,0.3); min-width: 300px;
      `;

      dialog.innerHTML = `
        <h3 style="margin: 0 0 16px 0; color: #333;">Nom du projet</h3>
        <input type="text" id="projectNameInput" value="${defaultName}" 
               style="width: 100%; padding: 8px; border: 1px solid #ccc; border-radius: 4px; margin-bottom: 16px;">
        <div style="text-align: right;">
          <button id="cancelBtn" style="margin-right: 8px; padding: 6px 12px; border: 1px solid #ccc; background: #f5f5f5; border-radius: 4px;">Annuler</button>
          <button id="okBtn" style="padding: 6px 12px; border: none; background: #007bff; color: white; border-radius: 4px;">OK</button>
        </div>
      `;

      overlay.appendChild(dialog);
      document.body.appendChild(overlay);

      const input = document.getElementById('projectNameInput');
      const okBtn = document.getElementById('okBtn');
      const cancelBtn = document.getElementById('cancelBtn');

      input.focus();
      input.select();

      const cleanup = () => document.body.removeChild(overlay);

      okBtn.onclick = () => {
        const value = input.value.trim();
        cleanup();
        resolve(value || null);
      };

      cancelBtn.onclick = () => {
        cleanup();
        resolve(null);
      };

      input.onkeypress = (e) => {
        if (e.key === 'Enter') {
          const value = input.value.trim();
          cleanup();
          resolve(value || null);
        }
        if (e.key === 'Escape') {
          cleanup();
          resolve(null);
        }
      };
    });
  };

  // Fonctions pour les fichiers Markdown
  const handleCopyMarkdown = async () => {
    const editor = document.querySelector('.editor-scroll .prose');
    if (!editor) return;

    const markdown = htmlToMarkdown(content);

    try {
      await navigator.clipboard.writeText(markdown);
      console.log('Markdown copi√© dans le presse-papier');
    } catch (err) {
      console.error('Erreur lors de la copie:', err);
    }
  };

  // G√©n√©rer un nom de fichier unique avec timestamp
  const generateImageFileName = (originalName, extension) => {
    const now = new Date();
    const timestamp = now.getFullYear().toString() + 
                     String(now.getMonth() + 1).padStart(2, '0') +
                     String(now.getDate()).padStart(2, '0') + '_' +
                     String(now.getHours()).padStart(2, '0') +
                     String(now.getMinutes()).padStart(2, '0') +
                     String(now.getSeconds()).padStart(2, '0');

    const baseName = originalName.replace(/\.[^/.]+$/, "") || 'image';
    return `${baseName}_${timestamp}.${extension}`;
  };

  // Extraire et sauvegarder les images blob
  const extractAndSaveImages = async (htmlContent, projectDir) => {
    // Trouver toutes les images avec src blob
    const blobImageRegex = /<img[^>]+src=["'](blob:[^"']+)["'][^>]*alt=["']([^"']*)["'][^>]*>/g;
    const matches = [...htmlContent.matchAll(blobImageRegex)];

    if (matches.length === 0) {
      return htmlContent; // Pas d'images blob
    }

    // Cr√©er le dossier images
    const imagesDir = await projectDir.getDirectoryHandle('images', { create: true });

    let updatedHtml = htmlContent;

    for (const match of matches) {
      const [fullMatch, blobUrl, altText] = match;

      try {
        // R√©cup√©rer le fichier depuis l'URL blob
        const response = await fetch(blobUrl);
        const blob = await response.blob();

        // D√©terminer l'extension du fichier
        const extension = blob.type.split('/')[1] || 'jpg';
        const fileName = generateImageFileName(altText || 'image', extension);

        // Sauvegarder l'image
        const imageHandle = await imagesDir.getFileHandle(fileName, { create: true });
        const writable = await imageHandle.createWritable();
        await writable.write(blob);
        await writable.close();

        // Remplacer dans le HTML par le chemin relatif
        const relativePath = `./images/${fileName}`;
        updatedHtml = updatedHtml.replace(blobUrl, relativePath);

        console.log('‚úÖ Image sauvegard√©e:', fileName);
      } catch (imageError) {
        console.error('‚ùå Erreur sauvegarde image:', imageError);
      }
    }

    return updatedHtml;
  };

  // Trouver un nom de dossier disponible (gestion des collisions)
  const findAvailableDirectoryName = async (parentDir, baseName) => {
    let finalName = baseName;
    let counter = 1;

    while (true) {
      try {
        // Tenter d'acc√©der au dossier (si √ßa marche, il existe d√©j√†)
        await parentDir.getDirectoryHandle(finalName);
        // Le dossier existe, essayer le suivant
        finalName = `${baseName} (${counter})`;
        counter++;
      } catch (error) {
        // Le dossier n'existe pas, on peut l'utiliser
        break;
      }
    }

    return finalName;
  };

  const handleSaveMarkdown = async () => {
    if (!isFileSystemAccessSupported()) {
      alert('‚ùå Votre navigateur ne supporte pas l\'acc√®s aux fichiers.');
      return;
    }

    try {
      const htmlContent = editorRef.current?.innerHTML || content;

      // Demander le nom du projet avec input HTML
      const projectName = await showProjectNameInput();

      if (!projectName || projectName.trim() === '') {
        return;
      }

      // S√©lectionner le r√©pertoire parent - ouvrir dans Documents par d√©faut
      const parentDirectoryHandle = await window.showDirectoryPicker({
        mode: 'readwrite',
        startIn: 'documents'
      });

      // Trouver un nom de dossier disponible (√©viter les collisions)
      const availableProjectName = await findAvailableDirectoryName(
        parentDirectoryHandle, 
        projectName.trim()
      );

      // D√©tecter et m√©moriser le dossier PromptoDYS si on y est
      try {
        const parentName = parentDirectoryHandle.name;
        if (parentName.toLowerCase().includes('promptodys') || 
            parentName.toLowerCase().includes('prompt')) {
          storePromptoDYSPath(parentName);
        }
      } catch (error) {
        // Ignorer les erreurs de stockage, ce n'est pas critique
      }

      // Cr√©er le dossier du projet avec le nom disponible
      const projectDir = await parentDirectoryHandle.getDirectoryHandle(
        availableProjectName, 
        { create: true }
      );

      // Convertir les blob URLs vers indexed:// URLs pour pr√©server les images IndexedDB
      const htmlWithIndexedUrls = convertBlobUrlsToIndexed(htmlContent);

      // Extraire et sauvegarder les images, puis mettre √† jour le HTML
      const updatedHtmlContent = await extractAndSaveImages(htmlWithIndexedUrls, projectDir);

      // Convertir en markdown avec les nouveaux chemins d'images
      const markdownContent = htmlToMarkdown(updatedHtmlContent);

      // Sauvegarder le fichier markdown (utiliser le nom disponible)
      const fileName = `${availableProjectName}.md`;
      const fileHandle = await projectDir.getFileHandle(fileName, { create: true });
      const writable = await fileHandle.createWritable();
      await writable.write(markdownContent);
      await writable.close();

      const imageCount = (updatedHtmlContent.match(/src=["']\.\/images\/[^"']+["']/g) || []).length;
      const message = imageCount > 0 
        ? `‚úÖ Projet sauvegard√© avec succ√®s :\n${fileName} + ${imageCount} image(s) dans ${availableProjectName}/`
        : `‚úÖ Projet sauvegard√© avec succ√®s :\n${fileName} dans ${availableProjectName}/`;

      alert(message);
    } catch (error) {
      console.error('‚ùå Erreur sauvegarde:', error);
      if (error.name === 'AbortError') {
        return;
      }
      alert(`‚ùå Erreur lors de la sauvegarde :\n${error.message}`);
    }
  };

  const handleOpenProject = async () => {
    if (!isFileSystemAccessSupported()) {
      alert('‚ùå Votre navigateur ne supporte pas l\'acc√®s aux fichiers.');
      return;
    }

    try {
      // S√©lectionner le r√©pertoire du projet
      const directoryHandle = await window.showDirectoryPicker({
        mode: 'read'
      });

      // Chercher le fichier .md correspondant au nom du r√©pertoire
      const projectName = directoryHandle.name;
      const fileName = `${projectName}.md`;

      try {
        const fileHandle = await directoryHandle.getFileHandle(fileName);
        const file = await fileHandle.getFile();
        const content = await file.text();
        let htmlContent = markdownToHtml(content);

        // Charger les images locales et les convertir en blob URLs
        htmlContent = await loadLocalImages(htmlContent, directoryHandle);

        if (editorRef.current) {
          editorRef.current.innerHTML = htmlContent;
          // D√©clencher l'√©v√©nement de changement pour mettre √† jour l'√©tat
          const inputEvent = new Event('input', { bubbles: true });
          editorRef.current.dispatchEvent(inputEvent);
        }

        // Basculer en mode WYSIWYG apr√®s le chargement
        onViewModeChange('wysiwyg');

        const imageCount = (htmlContent.match(/src=["']blob:[^"']+["']/g) || []).length;
        const message = imageCount > 0 
          ? `‚úÖ Projet ouvert avec succ√®s :\n${fileName} + ${imageCount} image(s) charg√©e(s)`
          : `‚úÖ Projet ouvert avec succ√®s :\n${fileName}`;

        alert(message);
      } catch (fileError) {
        // Si le fichier n'existe pas, proposer de le cr√©er
        const createFile = window.confirm(`Le fichier ${fileName} n'existe pas dans ce r√©pertoire.\n\nVoulez-vous cr√©er un nouveau projet ?`);
        if (createFile) {
          const newContent = `# ${projectName}\n\nContenu de votre projet...`;
          const htmlContent = markdownToHtml(newContent);

          if (editorRef.current) {
            editorRef.current.innerHTML = htmlContent;
            const inputEvent = new Event('input', { bubbles: true });
            editorRef.current.dispatchEvent(inputEvent);
          }

          // Basculer en mode WYSIWYG apr√®s le chargement
          onViewModeChange('wysiwyg');
          alert(`üìù Nouveau projet cr√©√© : ${projectName}\n\nUtilisez "Sauvegarder" pour l'enregistrer.`);
        }
      }
    } catch (error) {
      console.error('‚ùå Erreur ouverture projet:', error);
      if (error.name === 'AbortError') {
        // L'utilisateur a annul√©
        return;
      }
      alert(`‚ùå Erreur lors de l'ouverture du projet :\n${error.message}`);
    }
  };

  const loadLocalImages = async (htmlContent, projectDir) => {
    // Trouver toutes les images avec src="./images/*"
    const localImageRegex = /<img[^>]+src=["']\.\/(images\/[^"']+)["'][^>]*>/g;
    const matches = [...htmlContent.matchAll(localImageRegex)];

    if (matches.length === 0) {
      return htmlContent; // Pas d'images locales
    }

    let updatedHtml = htmlContent;

    try {
      // V√©rifier si le dossier images existe
      const imagesDir = await projectDir.getDirectoryHandle('images');

      for (const match of matches) {
        const [fullMatch, imagePath] = match;
        const fileName = imagePath.split('/')[1]; // R√©cup√©rer juste le nom du fichier

        try {
          // Charger l'image depuis le dossier
          const imageHandle = await imagesDir.getFileHandle(fileName);
          const imageFile = await imageHandle.getFile();

          // Cr√©er une blob URL pour l'affichage
          const blobUrl = URL.createObjectURL(imageFile);

          // Remplacer dans le HTML
          updatedHtml = updatedHtml.replace(`"./${imagePath}"`, `"${blobUrl}"`);

          console.log('‚úÖ Image locale charg√©e:', fileName);
        } catch (imageError) {
          console.error('‚ùå Image introuvable:', fileName, imageError);
        }
      }
    } catch (dirError) {
      console.log('‚ÑπÔ∏è Dossier images non trouv√©');
    }

    return updatedHtml;
  };

  const handleLoadMarkdown = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const markdownContent = e.target.result;
        const htmlContent = markdownToHtml(markdownContent);

        if (editorRef.current) {
          editorRef.current.innerHTML = htmlContent;
          // D√©clencher l'√©v√©nement de changement pour mettre √† jour l'√©tat
          const inputEvent = new Event('input', { bubbles: true });
          editorRef.current.dispatchEvent(inputEvent);
        }

        // Basculer en mode WYSIWYG apr√®s le chargement
        onViewModeChange('wysiwyg');
        alert('Fichier Markdown charg√© avec succ√®s !');
      } catch (error) {
        console.error('Erreur lors du chargement:', error);
        alert('Erreur lors du chargement du fichier');
      }
    };

    reader.readAsText(file);
    // Reset l'input pour permettre de recharger le m√™me fichier
    event.target.value = '';
  };

  const colors = [
    { name: 'Noir', value: '#000000' },
    { name: 'Bleu', value: '#3399ff' },
    { name: 'Vert', value: '#00cc00' },
    { name: 'Rouge', value: '#ff0000' },
    { name: 'Orange', value: '#ff9900' },
    { name: 'Violet', value: '#ff00ff' }
  ];


  const execCommand = (command, value = null) => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    // Traitement uniforme - √âtat d'abord, DOM apr√®s
    const newFormat = { ...currentFormat };

    if (command === 'bold') {
      newFormat.bold = !currentFormat.bold;
      document.execCommand('bold', false, null);
    } else if (command === 'italic') {
      newFormat.italic = !currentFormat.italic;
      document.execCommand('italic', false, null);
    } else if (command === 'normal') {
      // √âtat Normal imm√©diat - FORCE tout √† false
      newFormat.bold = false;
      newFormat.italic = false;
      newFormat.color = '#000000';
      newFormat.fontSize = '16px';
      newFormat.fontFamily = 'system-ui';
      newFormat.heading = null;
      newFormat.list = null;

      // Force d√©sactivation DOM - toujours ex√©cuter m√™me si d√©j√† off
      if (document.queryCommandState('bold')) {
        document.execCommand('bold', false, null);
      }
      if (document.queryCommandState('italic')) {
        document.execCommand('italic', false, null);
      }
      document.execCommand('removeFormat', false, null);
      document.execCommand('formatBlock', false, 'p');
    } else if (command === 'foreColor') {
      newFormat.color = value;
      document.execCommand('foreColor', false, value);
    } else {
      document.execCommand(command, false, value);
    }

    // Mise √† jour imm√©diate AVEC force refresh
    onFormatChange(newFormat);

    // Pour le bouton Normal, forcer une deuxi√®me mise √† jour apr√®s DOM
    if (command === 'normal') {
      setTimeout(() => {
        onFormatChange({
          bold: false,
          italic: false,
          color: '#000000',
          fontSize: '16px',
          fontFamily: 'system-ui',
          heading: null,
          list: null
        });
      }, 10);
    }

    // Ignore court pour tous les boutons
    ignoreSelectionChangeRef.current = true;
    setTimeout(() => {
      ignoreSelectionChangeRef.current = false;
    }, 100);
  };

  const handleHeading = (level) => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    // Nettoyer le formatage existant avant d'appliquer le titre
    document.execCommand('removeFormat', false, null);

    // Appliquer le titre
    document.execCommand('formatBlock', false, level);

    onFormatChange({ bold: false, italic: false, color: '#000000', fontSize: '16px', fontFamily: 'system-ui', heading: level, list: null });
  };


  const handleList = (type) => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    if (type === 'bullet') {
      document.execCommand('insertUnorderedList', false, null);
    } else if (type === 'number') {
      document.execCommand('insertOrderedList', false, null);
    } else if (type === 'letter') {
      document.execCommand('insertOrderedList', false, null);
      // Appliquer le style alphab√©tique √† la liste cr√©√©e
      const selection = window.getSelection();
      if (selection.rangeCount > 0) {
        let element = selection.getRangeAt(0).commonAncestorContainer;
        while (element && element.nodeType !== 1) {
          element = element.parentNode;
        }
        while (element && element.tagName !== 'OL') {
          element = element.parentNode;
        }
        if (element && element.tagName === 'OL') {
          element.style.listStyleType = 'lower-alpha';
        }
      }
    }

    onFormatChange({ bold: false, italic: false, color: '#000000', fontSize: '16px', fontFamily: 'system-ui', heading: null, list: type });
  };

  const handleClearAll = () => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    // S√©lectionner tout le contenu puis le supprimer
    document.execCommand('selectAll', false, null);
    document.execCommand('delete', false, null);

    onFormatChange({ bold: false, italic: false, color: '#000000', fontSize: '16px', fontFamily: 'system-ui', heading: null, list: null });
  };

  const handleCopy = () => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();
    document.execCommand('copy', false, null);
  };

  const handlePaste = () => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();
    document.execCommand('paste', false, null);

    // Forcer la mise √† jour apr√®s collage
    setTimeout(() => onFormatChange(), 10);
  };

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file || !file.type.startsWith('image/')) {
      alert('Veuillez s√©lectionner un fichier image valide.');
      return;
    }

    // Ins√©rer l'image directement dans l'√©diteur
    if (viewMode === 'wysiwyg' && editorRef.current) {
      try {
        // Cr√©er une URL blob temporaire pour l'affichage imm√©diat
        const tempBlobUrl = URL.createObjectURL(file);

        // Stocker l'image dans IndexedDB et r√©cup√©rer l'ID
        const imageId = await storeImage(tempBlobUrl, file.name);

        // Ins√©rer avec blob: URL pour √©viter ERR_UNKNOWN_URL_SCHEME
        // La conversion indexed:// se fera √† la sauvegarde
        editorRef.current.focus();
        const imgHtml = `<img src="${tempBlobUrl}" style="max-width: 100%; height: auto; margin: 0.5em 0;" alt="${file.name}" data-image-id="${imageId}" />`;
        document.execCommand('insertHTML', false, imgHtml);
        onFormatChange({ bold: false, italic: false, color: '#000000', fontSize: '16px', fontFamily: 'system-ui', heading: null, list: null });

        console.log('‚úÖ Image ins√©r√©e et stock√©e:', file.name, imageId);
        event.target.value = '';

        // Lib√©rer la blob URL temporaire apr√®s un court d√©lai
        setTimeout(() => {
          URL.revokeObjectURL(tempBlobUrl);
        }, 1000);

      } catch (error) {
        console.error('‚ùå Erreur insertion image:', error);
        alert('Erreur lors de l\'insertion de l\'image');
      }
    }
  };


  return (
    <div className="border-4 border-blue-600 rounded-lg bg-blue-100 py-3 shadow-lg w-full">
      {/* Ligne 1 - Format + Couleurs + Mode */}
      <div className="flex items-center justify-start space-x-6 mb-2 w-full">
        {/* Format buttons */}
        <div className="flex items-center space-x-2">
          <span className="text-sm font-medium">Format:</span>
          <button
