import React, { useCallback, useEffect, useState } from 'react';
import { detectBase64, applyBase64Collapse } from '../utils/base64Collapse';
import { convertAllPromptoDysUrlsToBlobs, getCurrentProject } from '../utils/promptoDysManager';
import useIndexedStorage from '../hooks/useIndexedStorage';

const Editor = ({ 
  viewMode, 
  content, 
  editorRef, 
  onInput, 
  onSelectionChange, 
  currentFormat, 
  convertIndexedUrlsToBlob,
  selectedImage,
  onImageClick,
  onEditorClick,
  onDeleteSelectedImage
}) => {

  // √âtat pour g√©rer l'expansion des donn√©es Base64
  const [expandedBase64, setExpandedBase64] = useState({});

  // Hook pour g√©rer les images IndexedDB
  const { loadImage } = useIndexedStorage();

  // Gestionnaire pour l'auto-redimensionnement (toujours d√©clar√©)
  const handleTextareaResize = useCallback((textarea) => {
    if (textarea) {
      textarea.style.height = 'auto';
      textarea.style.height = Math.max(384, textarea.scrollHeight) + 'px';
    }
  }, []);

  // Gestionnaire pour toggle Base64
  const handleBase64Toggle = useCallback((e) => {
    const clickedText = e.target.value;
    const cursorPos = e.target.selectionStart;

    // Chercher si on a cliqu√© sur un bouton "üëÅÔ∏è Voir"
    const beforeCursor = clickedText.substring(Math.max(0, cursorPos - 100), cursorPos + 20);
    const toggleMatch = beforeCursor.match(/üëÅÔ∏è Voir/);

    if (toggleMatch) {
      // Trouver l'index du Base64 correspondant
      const base64Matches = detectBase64(content);

      // Pour simplifier, on toggle le premier Base64 trouv√©
      // Dans une version plus avanc√©e, on pourrait calculer l'index exact
      if (base64Matches.length > 0) {
        const toggleIndex = 0; // Simplifi√© pour cette version
        setExpandedBase64(prev => ({
          ...prev,
          [toggleIndex]: !prev[toggleIndex]
        }));
      }
    }
  }, [content]);

  // Fonction pour obtenir le contenu avec Base64 collaps√©s
  const getCollapsedContent = useCallback((originalContent) => {
    if (viewMode === 'wysiwyg') {
      return originalContent; // Pas de collapse en WYSIWYG
    }
    return applyBase64Collapse(originalContent, expandedBase64);
  }, [viewMode, expandedBase64]);

  // Fonction pour formater le HTML avec des retours √† la ligne et indentation
  const formatHtmlForSource = useCallback((html) => {
    let indentLevel = 0;
    const indentSize = 2; // 2 espaces par niveau

    return html
      .replace(/<br\s*\/?>/gi, '<br>\n')
      .replace(/<\/?(h[1-6]|p|div|ul|ol|li|strong|em|span)[^>]*>/gi, (match) => {
        if (match.startsWith('</')) {
          // Balise fermante : diminuer l'indentation puis ajouter la balise
          indentLevel = Math.max(0, indentLevel - 1);
          return match + '\n';
        } else {
          // Balise ouvrante : ajouter la balise puis augmenter l'indentation
          const result = '\n' + ' '.repeat(indentLevel * indentSize) + match;
          // Augmenter l'indentation pour les balises conteneurs
          if (match.match(/<(ul|ol|li|div|p|h[1-6])[^>]*>/i)) {
            indentLevel++;
          }
          return result;
        }
      })
      .split('\n')
      .map((line, index) => {
        if (index === 0) return line.trim(); // Premi√®re ligne sans indentation
        if (line.trim() === '') return ''; // Ligne vide
        if (line.trim().startsWith('</')) {
          // Balise fermante : r√©duire l'indentation
          const currentIndent = Math.max(0, indentLevel - 1);
          return ' '.repeat(currentIndent * indentSize) + line.trim();
        }
        // Contenu texte : utiliser l'indentation actuelle
        return line.startsWith(' ') ? line : ' '.repeat(indentLevel * indentSize) + line.trim();
      })
      .join('\n')
      .replace(/^\n+/, '') // Supprimer les retours √† la ligne en d√©but
      .replace(/\n{2,}/g, '\n') // Supprimer toutes les lignes vides multiples
      .trim();
  }, []);

  // Gestionnaire unifi√© pour tous les changements
  const handleChange = useCallback((e) => {
    if (viewMode === 'wysiwyg' && editorRef.current) {
      // Nettoyer les spans vides et les &nbsp; en trop
      const cleanContent = (html) => {
        return html
          .replace(/<span(?![^>]*style="[^"]*color:)[^>]*>\s*<\/span>/g, '') // Supprimer spans vides SAUF les spans color√©s
          .replace(/(&nbsp;\s*){2,}/g, '&nbsp;') // R√©duire les &nbsp; multiples
          .replace(/<font[^>]*color="([^"]*)"[^>]*>(.*?)<\/font>/g, '<span style="color: $1;">$2</span>') // Convertir font en span
          .replace(/<b\b[^>]*>(.*?)<\/b>/g, '<strong>$1</strong>') // Convertir b en strong
          .replace(/<i\b[^>]*>(.*?)<\/i>/g, '<em>$1</em>'); // Convertir i en em
      };

      const newContent = cleanContent(editorRef.current.innerHTML);

      // √âviter les boucles infinies avec une v√©rification plus stricte
      if (newContent !== content && newContent.trim() !== content.trim()) {
        onInput({ target: { innerHTML: newContent, value: newContent } });
      }

      // Re-rendre MathJax apr√®s modification du contenu
      if (window.MathJax && window.MathJax.typesetPromise) {
        window.MathJax.typesetPromise([editorRef.current]).catch((err) => {
          console.warn('Erreur MathJax:', err);
        });
      }

      // Mettre √† jour l'√©tat de formatage avec un d√©lai pour √©viter les conflits
      requestAnimationFrame(() => {
        if (document.activeElement === editorRef.current) {
          onSelectionChange();
        }
      });
    }
  }, [viewMode, editorRef, onInput, onSelectionChange, content]);

  // Gestionnaire pour les √©v√©nements de s√©lection/curseur
  const handleSelectionChange = useCallback(() => {
    if (viewMode === 'wysiwyg' && document.activeElement === editorRef.current) {
      onSelectionChange();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [viewMode, onSelectionChange]);


  // Initialiser le contenu uniquement si n√©cessaire
  useEffect(() => {
    const initializeContent = async () => {
      if (viewMode === 'wysiwyg' && editorRef.current && editorRef.current.innerHTML !== content) {
        // Ne pas modifier le contenu si l'utilisateur interagit activement
        const hasFocus = document.activeElement === editorRef.current;
        const hasSelection = window.getSelection().rangeCount > 0;

        // √âviter les mises √† jour pendant l'interaction utilisateur
        if (hasFocus && hasSelection) {
          return; // Ne pas perturber l'utilisateur
        }

        // Convertir les URLs relatives PromptoDYS en Blob URLs pour affichage
        const currentProject = getCurrentProject();
        let displayContent = content;

        if (currentProject.directory) {
          displayContent = await convertAllPromptoDysUrlsToBlobs(content, currentProject.directory);
        }

        // La conversion indexed:// ‚Üí blob: est d√©j√† g√©r√©e par App.js au chargement initial

        // Mise √† jour avec le contenu converti (IndexedDB g√©r√© par MutationObserver)
        editorRef.current.innerHTML = displayContent;

        // Re-rendre MathJax apr√®s mise √† jour du contenu
        if (window.MathJax && window.MathJax.typesetPromise) {
          window.MathJax.typesetPromise([editorRef.current]).catch((err) => {
            console.warn('Erreur MathJax:', err);
          });
        }
      }
    };

    initializeContent();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [viewMode, content]);

  // Gestionnaire pour la copie - convertir HTML en texte propre et g√©rer les images s√©lectionn√©es
  const handleCopy = useCallback((e) => {
    if (viewMode === 'wysiwyg' && editorRef.current) {
      // Priorit√© 1: Si une image est s√©lectionn√©e, copier l'image
      if (selectedImage) {
        try {
          // Cr√©er un √©l√©ment temporaire avec l'image
          const tempDiv = document.createElement('div');
          const clonedImg = selectedImage.cloneNode(true);
          tempDiv.appendChild(clonedImg);

          // Copier au format HTML pour pr√©server la structure
          e.clipboardData.setData('text/html', tempDiv.innerHTML);
          // Copier aussi le src comme texte de fallback
          e.clipboardData.setData('text/plain', selectedImage.src);
          e.preventDefault();
          return;
        } catch (error) {
          console.warn('Erreur copie image:', error);
        }
      }

      // Priorit√© 2: S√©lection de texte classique
      const selection = window.getSelection();
      if (selection.rangeCount > 0) {
        const range = selection.getRangeAt(0);
        const selectedContent = range.cloneContents();
        const tempDiv = document.createElement('div');
        tempDiv.appendChild(selectedContent);

        // Convertir HTML en texte avec retours √† la ligne pr√©serv√©s
        const htmlContent = tempDiv.innerHTML;
        const textContent = htmlContent
          .replace(/<div[^>]*>/gi, '')
          .replace(/<\/div>/gi, '\n')
          .replace(/<br\s*\/?>/gi, '\n')
          .replace(/<p[^>]*>/gi, '')
          .replace(/<\/p>/gi, '\n')
          .replace(/<[^>]+>/g, '') // Supprimer toutes les autres balises
          .replace(/&nbsp;/g, ' ')
          .replace(/&amp;/g, '&')
          .replace(/&lt;/g, '<')
          .replace(/&gt;/g, '>')
          .replace(/&quot;/g, '"')
          .replace(/&#39;/g, "'")
          .replace(/\n+$/, ''); // Supprimer les retours √† la ligne en fin

        // Mettre le texte propre dans le presse-papier
        e.clipboardData.setData('text/plain', textContent);
        e.preventDefault();
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [viewMode, selectedImage]);

  // Gestionnaire pour forcer le refresh apr√®s coller
  const handlePaste = useCallback((e) => {
    if (viewMode === 'wysiwyg' && editorRef.current) {
      // Attendre que le contenu soit coll√©
      setTimeout(async () => {
        console.log('üîÑ Refresh forc√© apr√®s coller');

        // Reconvertir toutes les images indexed:// qui pourraient avoir √©t√© coll√©es
        const indexedImages = editorRef.current.querySelectorAll('img[src^="indexed://"]');
        for (const img of indexedImages) {
          const imageId = img.src.replace('indexed://', '');
          try {
            const imageData = await loadImage(imageId);
            if (imageData?.blobUrl) {
              img.src = imageData.blobUrl;
              img.setAttribute('data-image-id', imageId);
              console.log('‚úÖ Image refresh apr√®s coller:', imageId);
            }
          } catch (error) {
            console.warn('‚ö†Ô∏è Erreur refresh image apr√®s coller:', error);
          }
        }

        // Ajouter les poign√©es aux nouvelles images coll√©es
        const newImages = editorRef.current.querySelectorAll('img:not([data-resizable])');
        newImages.forEach(img => {
          if (editorRef.current.addResizeHandlesToImage) {
            editorRef.current.addResizeHandlesToImage(img);
          }
        });

        // D√©clencher sauvegarde
        if (onInput) {
          const event = new Event('input', { bubbles: true });
          editorRef.current.dispatchEvent(event);
        }
      }, 100);
    }
  }, [viewMode, loadImage, onInput]);

  // √âcouter les changements de s√©lection, la copie et le coller
  useEffect(() => {
    if (viewMode === 'wysiwyg') {
      document.addEventListener('selectionchange', handleSelectionChange);
      editorRef.current?.addEventListener('copy', handleCopy);
      editorRef.current?.addEventListener('paste', handlePaste);

      return () => {
        document.removeEventListener('selectionchange', handleSelectionChange);
        editorRef.current?.removeEventListener('copy', handleCopy);
        editorRef.current?.removeEventListener('paste', handlePaste);
      };
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [viewMode, handleSelectionChange, handleCopy, handlePaste]);

  // Effect pour redimensionner au changement de contenu (toujours d√©clar√©)
  useEffect(() => {
    if (viewMode !== 'wysiwyg' && editorRef.current) {
      handleTextareaResize(editorRef.current);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [content, viewMode, handleTextareaResize]);

  // Fonction pour convertir toutes les images indexed:// pr√©sentes dans le DOM
  const convertAllIndexedImages = useCallback(async () => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    const images = editorRef.current.querySelectorAll('img[src^="indexed://"]');
    console.log(`üîç DIAGNOSTIC: ${images.length} images indexed:// trouv√©es dans le DOM`);

    for (const img of images) {
      const imageId = img.src.replace('indexed://', '');
      console.log('üîé DIAGNOSTIC: Tentative chargement imageId:', imageId);

      try {
        const imageData = await loadImage(imageId);
        console.log('üîé DIAGNOSTIC: R√©sultat loadImage:', imageData);

        if (imageData && imageData.blobUrl) {
          // Tester si la blob URL est valide
          const testImg = new Image();
          testImg.onload = () => console.log('‚úÖ DIAGNOSTIC: Blob URL valide:', imageData.blobUrl);
          testImg.onerror = () => console.error('‚ùå DIAGNOSTIC: Blob URL invalide:', imageData.blobUrl);
          testImg.src = imageData.blobUrl;

          img.src = imageData.blobUrl;
          console.log('‚úÖ Image convertie au chargement:', imageId, '‚Üí', imageData.blobUrl);

          // R√©appliquer les poign√©es apr√®s conversion
          setTimeout(() => {
            if (editorRef.current?.contains(img) && editorRef.current.addResizeHandlesToImage) {
              editorRef.current.addResizeHandlesToImage(img);
            }
          }, 100);
        } else {
          console.error('‚ùå DIAGNOSTIC: loadImage a retourn√© null/undefined pour:', imageId);
        }
      } catch (error) {
        console.error('‚ùå DIAGNOSTIC: Erreur loadImage pour', imageId, ':', error);
      }
    }
  }, [viewMode, loadImage]);

  // MutationObserver pour convertir les images indexed:// en temps r√©el
  useEffect(() => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    // Conversion initiale des images d√©j√† pr√©sentes
    convertAllIndexedImages().then(() => {
      // Ajouter les poign√©es apr√®s la conversion
      setTimeout(() => {
        const addHandles = () => {
          const images = editorRef.current?.querySelectorAll('img:not([data-resizable])');
          images?.forEach((img) => {
            if (!img.src.startsWith('indexed://')) {
              img.setAttribute('data-resizable', 'true');
              // D√©clencher l'ajout des poign√©es
              const event = new Event('load', { bubbles: true });
              img.dispatchEvent(event);
            }
          });
        };
        addHandles();
      }, 500);
    });

    const observer = new MutationObserver(async (mutations) => {
      // Traiter les mutations de fa√ßon s√©quentielle pour √©viter les race conditions
      for (const mutation of mutations) {
        // V√©rifier les nouveaux n≈ìuds ajout√©s
        for (const node of mutation.addedNodes) {
          if (node.nodeType === Node.ELEMENT_NODE) {
            // Chercher les images avec src indexed://
            const images = node.tagName === 'IMG' 
              ? [node] 
              : node.querySelectorAll ? node.querySelectorAll('img[src^="indexed://"]') : [];

            for (const img of images) {
              if (img.src && img.src.startsWith('indexed://')) {
                const imageId = img.src.replace('indexed://', '');
                console.log('üîç Image IndexedDB d√©tect√©e (mutation):', imageId);

                try {
                  // Attendre que loadImage soit compl√®tement r√©solu
                  const imageData = await loadImage(imageId);
                  if (imageData && imageData.blobUrl) {
                    // V√©rifier que la blob URL est valide avant l'assignation
                    img.src = imageData.blobUrl;
                    console.log('‚úÖ Image DOM convertie (mutation):', imageId, '‚Üí', imageData.blobUrl);
                  } else {
                    console.warn('‚ö†Ô∏è Pas de blob URL g√©n√©r√©e pour:', imageId);
                  }
                } catch (error) {
                  console.warn('‚ö†Ô∏è Erreur conversion image (mutation):', imageId, error);
                }
              }
            }
          }
        }

        // V√©rifier les modifications d'attributs (src modifi√©)
        if (mutation.type === 'attributes' && mutation.attributeName === 'src') {
          const img = mutation.target;
          if (img.tagName === 'IMG' && img.src && img.src.startsWith('indexed://')) {
            const imageId = img.src.replace('indexed://', '');
            console.log('üîç Attribut src modifi√© vers IndexedDB:', imageId);

            try {
              // Attendre que loadImage soit compl√®tement r√©solu
              const imageData = await loadImage(imageId);
              if (imageData && imageData.blobUrl) {
                // V√©rifier que la blob URL est valide avant l'assignation
                img.src = imageData.blobUrl;
                console.log('‚úÖ Image DOM convertie (attr):', imageId, '‚Üí', imageData.blobUrl);
              } else {
                console.warn('‚ö†Ô∏è Pas de blob URL g√©n√©r√©e pour:', imageId);
              }
            } catch (error) {
              console.warn('‚ö†Ô∏è Erreur conversion image (attr):', imageId, error);
            }
          }
        }
      }
    });

  // Observer les modifications dans l'√©diteur
  observer.observe(editorRef.current, {
    childList: true,
    subtree: true,
    attributes: true,
    attributeFilter: ['src']
  });

  return () => {
    observer.disconnect();
  };
}, [viewMode, loadImage, convertAllIndexedImages]);

  // Effect pour appliquer le style de s√©lection d'image
  useEffect(() => {
    if (viewMode === 'wysiwyg' && editorRef.current) {
      // Supprimer la classe de toutes les images
      const allImages = editorRef.current.querySelectorAll('img');
      allImages.forEach(img => {
        const wrapper = img.closest('.resizable-image');
        if (wrapper) {
          wrapper.classList.remove('image-selected');
        }
      });

      // Ajouter la classe √† l'image s√©lectionn√©e
      if (selectedImage) {
        const wrapper = selectedImage.closest('.resizable-image');
        if (wrapper) {
          wrapper.classList.add('image-selected');
        }
      }
    }
  }, [selectedImage, viewMode]);

  // Gestion des poign√©es de redimensionnement pour les images
  useEffect(() => {
    if (viewMode === 'wysiwyg' && editorRef.current) {
      const addResizeHandlesToImage = (img) => {
        // Exposer la fonction pour r√©utilisation
        editorRef.current.addResizeHandlesToImage = addResizeHandlesToImage;
        // Ignorer les images indexed:// non converties
        if (img.src.startsWith('indexed://')) {
          console.log('‚è≥ Image indexed:// ignor√©e, en attente de conversion:', img.src);
          return;
        }

        // V√©rifier si d√©j√† trait√©
        if (img.getAttribute('data-resizable') || img.parentElement?.classList.contains('resizable-image')) {
          return;
        }

        // Attendre que l'image soit charg√©e
        if (!img.complete || img.naturalWidth === 0) {
          img.addEventListener('load', () => addResizeHandlesToImage(img), { once: true });
          return;
        }

        img.setAttribute('data-resizable', 'true');

        // Wrapper l'image dans un conteneur block-level non-√©ditable
        const wrapper = document.createElement('div');
        wrapper.className = 'image-block resizable-image';
        wrapper.setAttribute('contenteditable', 'false');

        // Ajouter gestionnaire de clic pour s√©lection d'image
        img.addEventListener('click', (e) => {
          e.stopPropagation();
          if (onImageClick) {
            onImageClick(img);
          }
        });

        // Extraire les dimensions depuis le style inline ou les attributs
        let currentWidth, currentHeight;

        // Priorit√© 1: attributs HTML (ex: width="300px")
        const attrWidth = img.getAttribute('width');
        const attrHeight = img.getAttribute('height');

        // Priorit√© 2: style inline (ex: style="width: 300px")
        const styleWidth = img.style.width;
        const styleHeight = img.style.height;

        if (attrWidth && attrHeight) {
          currentWidth = parseInt(attrWidth);
          currentHeight = parseInt(attrHeight);
          console.log('üìè Dimensions depuis attributs:', currentWidth, 'x', currentHeight);
        } else if (styleWidth && styleHeight) {
          currentWidth = parseInt(styleWidth);
          currentHeight = parseInt(styleHeight);
          console.log('üìè Dimensions depuis style:', currentWidth, 'x', currentHeight);
        } else {
          // Fallback: dimensions naturelles
          currentWidth = img.naturalWidth || 300;
          currentHeight = img.naturalHeight || 200;
          console.log('üìè Dimensions naturelles:', currentWidth, 'x', currentHeight);
        }

        // Limiter la hauteur maximale √† 300px en pr√©servant le ratio d'aspect
        const MAX_HEIGHT = 300;
        if (currentHeight > MAX_HEIGHT) {
          const aspectRatio = currentWidth / currentHeight;
          currentHeight = MAX_HEIGHT;
          currentWidth = Math.round(currentHeight * aspectRatio);
          console.log('üîÑ Image redimensionn√©e pour hauteur max 300px:', currentWidth, 'x', currentHeight);

          // Appliquer les nouvelles dimensions √† l'image
          img.setAttribute('width', currentWidth + 'px');
          img.setAttribute('height', currentHeight + 'px');
          img.style.width = currentWidth + 'px';
          img.style.height = currentHeight + 'px';
        }

        // Appliquer les styles block-level avec animation
        wrapper.style.cssText = `
          display: block;
          position: relative;
          width: ${currentWidth}px;
          height: ${currentHeight}px;
          max-width: 100%;
          margin: 0.5em 0;
          border: 2px solid transparent;
          border-radius: 4px;
          transition: all 0.2s ease;
          cursor: pointer;
          background: rgba(0, 123, 255, 0.05);
        `;

        // S'assurer que l'image est non-√©ditable
        img.setAttribute('contenteditable', 'false');
        img.style.cssText = `
          display: block;
          width: 100%;
          height: 100%;
          object-fit: contain;
          pointer-events: none;
          border-radius: 2px;
        `;

          // Ins√©rer le wrapper
          img.parentNode.insertBefore(wrapper, img);
          wrapper.appendChild(img);

          // Cr√©er les 4 poign√©es
          ['nw', 'ne', 'sw', 'se'].forEach((corner) => {
            const handle = document.createElement('div');
            handle.className = `resize-handle ${corner}`;
            handle.dataset.corner = corner;

            handle.addEventListener('mousedown', (e) => {
              e.preventDefault();
              e.stopPropagation();

              const startX = e.clientX;
              const startY = e.clientY;
              const startWidth = wrapper.offsetWidth;
              const startHeight = wrapper.offsetHeight;
              const aspectRatio = startWidth / startHeight;

              const handleMouseMove = (e) => {
                const deltaX = e.clientX - startX;
                const deltaY = e.clientY - startY;

                let newWidth = startWidth;
                let newHeight = startHeight;

                // Calculer les nouvelles dimensions selon le coin
                switch(corner) {
                  case 'se': // Sud-Est
                    newWidth = Math.max(50, startWidth + deltaX);
                    newHeight = newWidth / aspectRatio;
                    break;
                  case 'sw': // Sud-Ouest
                    newWidth = Math.max(50, startWidth - deltaX);
                    newHeight = newWidth / aspectRatio;
                    break;
                  case 'ne': // Nord-Est
                    newWidth = Math.max(50, startWidth + deltaX);
                    newHeight = newWidth / aspectRatio;
                    break;
                  case 'nw': // Nord-Ouest
                    newWidth = Math.max(50, startWidth - deltaX);
                    newHeight = newWidth / aspectRatio;
                    break;
                }

                // Appliquer les nouvelles dimensions
                wrapper.style.width = newWidth + 'px';
                wrapper.style.height = newHeight + 'px';
                img.style.width = '100%';
                img.style.height = '100%';

                // Ajouter les attributs HTML width/height pour persistance lors des conversions
                img.setAttribute('width', Math.round(newWidth) + 'px');
                img.setAttribute('height', Math.round(newHeight) + 'px');
              };

              const handleMouseUp = () => {
                document.removeEventListener('mousemove', handleMouseMove);
                document.removeEventListener('mouseup', handleMouseUp);

                // D√©clencher l'√©v√©nement de changement pour sauvegarder
                if (onInput) {
                  const event = new Event('input', { bubbles: true });
                  editorRef.current.dispatchEvent(event);
                }
              };

              document.addEventListener('mousemove', handleMouseMove);
              document.addEventListener('mouseup', handleMouseUp);
            });

            wrapper.appendChild(handle);
          });
      };

      // Observer pour d√©tecter les nouvelles images
      const handleMutation = (mutations) => {
        mutations.forEach(mutation => {
          mutation.addedNodes.forEach(node => {
            if (node.nodeName === 'IMG') {
              addResizeHandlesToImage(node);
            } else if (node.querySelectorAll) {
              node.querySelectorAll('img').forEach(addResizeHandlesToImage);
            }
          });
        });
      };

      const observer = new MutationObserver(handleMutation);
      observer.observe(editorRef.current, {
        childList: true,
        subtree: true
      });

      // Traiter les images existantes
      editorRef.current.querySelectorAll('img').forEach(addResizeHandlesToImage);

      return () => observer.disconnect();
    }
  }, [viewMode, onInput, content]);

  // Effect supprim√© - La gestion des poign√©es est maintenant dans l'effect pr√©c√©dent

  // Rendu conditionnel avec switch/case pour garantir une seule vue
  console.log('üîÑ Editor.js - Rendu switch/case, viewMode:', viewMode);

  switch (viewMode) {
    case 'wysiwyg':
      console.log('üéØ SWITCH - Vue WYSIWYG');
      return (
        <div key="wysiwyg">
          <style>{`
            .editor-content h1 { 
              font-size: 2em; 
              font-weight: bold; 
              margin: 0.67em 0; 
              line-height: 1.2;
            }
            .editor-content h2 { 
              font-size: 1.5em; 
              font-weight: bold; 
              margin: 0.75em 0; 
              line-height: 1.3;
            }
            .editor-content h3 { 
              font-size: 1.17em; 
              font-weight: bold; 
              margin: 0.83em 0; 
              line-height: 1.4;
            }
            .editor-content p {
              margin: 0.5em 0;
            }
            .editor-content ul {
              margin: 0.5em 0;
              padding-left: 2em;
              list-style-type: disc;
            }
            .editor-content ol {
              margin: 0.5em 0;
              padding-left: 2em;
              list-style-type: decimal;
            }
            .editor-content ol[style*="lower-alpha"] {
              list-style-type: lower-alpha;
            }
            .editor-content li {
              margin: 0.25em 0;
            }

            /* Styles pour images redimensionnables */
            .editor-content img {
              max-width: 100%;
              height: auto;
              position: relative;
              cursor: pointer;
            }

            .resizable-image {
              position: relative;
              display: inline-block;
              border: 2px solid transparent;
              transition: all 0.2s ease;
            }

            .image-block {
              display: block !important;
              margin: 0.5em 0;
              outline: none;
            }

            .resizable-image:hover {
              border-color: #007bff;
              background: rgba(0, 123, 255, 0.08) !important;
              transform: scale(1.01);
            }

            .image-selected {
              border-color: #007bff !important;
              box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.3) !important;
              background: rgba(0, 123, 255, 0.1) !important;
              animation: imageSelectPulse 1.5s ease-in-out infinite alternate;
            }

            @keyframes imageSelectPulse {
              0% { box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.3); }
              100% { box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.6); }
            }

            .resizable-image img {
              width: 100%;
              height: 100%;
              display: block;
            }

            .resizable-image .resize-handle {
              position: absolute;
              width: 10px;
              height: 10px;
              background: #3b82f6;
              border: 2px solid white;
              border-radius: 50%;
              opacity: 0;
              cursor: nw-resize;
              transition: opacity 0.2s;
            }

            .resizable-image:hover .resize-handle {
              opacity: 1;
            }

            .resize-handle.nw { top: -6px; left: -6px; cursor: nw-resize; }
            .resize-handle.ne { top: -6px; right: -6px; cursor: ne-resize; }
            .resize-handle.sw { bottom: -6px; left: -6px; cursor: sw-resize; }
            .resize-handle.se { bottom: -6px; right: -6px; cursor: se-resize; }
          `}</style>
          <div
            ref={editorRef}
            contentEditable
            spellCheck={false}
            onInput={handleChange}
            onClick={onEditorClick}
            onKeyDown={onDeleteSelectedImage}
            suppressContentEditableWarning={true}
            className="editor-content p-2 focus:outline-none h-full"
            style={{ 
              fontFamily: 'var(--dys-font-family)',
              fontSize: 'var(--dys-font-size)',
              lineHeight: 'var(--dys-line-height)',
              color: 'var(--dys-text-color)',
              backgroundColor: 'var(--dys-bg-color)',
              overflow: 'auto',
              wordWrap: 'break-word',
              whiteSpace: 'pre-wrap',
              border: '1px solid #e5e7eb',
              paddingBottom: '2rem'
            }}
          />
        </div>
      );

    case 'markdown': {
      console.log('üìù SWITCH - Vue MARKDOWN');
      const displayContent = getCollapsedContent(content);

      return (
        <div key="markdown" className="relative">
          <textarea
            ref={(el) => {
              editorRef.current = el;
            }}
            value={displayContent}
            onChange={(e) => {
              const newValue = e.target.value;
              const originalWithCollapse = getCollapsedContent(content);

              if (newValue !== originalWithCollapse) {
                onInput({ ...e, target: { ...e.target, value: newValue } });
              }
            }}
            onClick={handleBase64Toggle}
            className="editor-content p-2 w-full h-full border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
            style={{
              fontFamily: 'var(--dys-font-family)',
              fontSize: 'var(--dys-font-size)',
              lineHeight: 'var(--dys-line-height)',
              color: 'var(--dys-text-color)',
              backgroundColor: 'var(--dys-bg-color)',
              overflow: 'auto',
              wordWrap: 'break-word',
              whiteSpace: 'pre-wrap',
              paddingBottom: '2rem'
            }}
            placeholder="√âcrivez en Markdown...

# Titre 1
## Titre 2
### Titre 3

**Gras** *Italique*

- Liste √† puces
1. Liste num√©rot√©e
a. Liste alphab√©tique

$E = mc^2$ (formule inline)
$$\\int_{-\\infty}^{\\infty} e^{-x^2} dx = \\sqrt{\\pi}$$ (formule block)"
          />
          {detectBase64(content).length > 0 && (
            <div className="absolute top-2 right-2 bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs">
              üì∑ {detectBase64(content).length} image(s) Base64
            </div>
          )}
        </div>
      );
    }

    case 'html': {
      console.log('üîß SWITCH - Vue HTML');
      const htmlDisplayContent = formatHtmlForSource(getCollapsedContent(content));

      return (
        <div key="html" className="relative">
          <textarea
            ref={(el) => {
              editorRef.current = el;
            }}
            value={htmlDisplayContent}
            onChange={(e) => {
              const newValue = e.target.value;
              const originalWithCollapse = formatHtmlForSource(getCollapsedContent(content));

              if (newValue !== originalWithCollapse) {
                onInput({ ...e, target: { ...e.target, value: newValue } });
              }
            }}
            onClick={handleBase64Toggle}
            className="w-full h-full p-2 focus:outline-none resize-none"
            style={{ 
              fontFamily: 'Monaco, "Cascadia Code", "Roboto Mono", monospace',
              overflow: 'auto',
              wordWrap: 'break-word',
              whiteSpace: 'pre-wrap',
              border: '1px solid #e5e7eb',
              paddingBottom: '2rem'
            }}
            placeholder="Code source HTML..."
          />
          {detectBase64(content).length > 0 && (
            <div className="absolute top-2 right-2 bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs">
              üì∑ {detectBase64(content).length} image(s) Base64
            </div>
          )}
        </div>
      );
    }

    default:
      console.log('‚ùå SWITCH - AUCUNE VUE CORRESPONDANTE, viewMode:', viewMode);
      return null;
  }
};

export default Editor;
