import { useState, useCallback, useRef } from 'react';

const DB_NAME = 'EditorStorage';
const DB_VERSION = 1;
const DOCUMENTS_STORE = 'documents';

/**
 * Hook de stockage de documents avec IndexedDB backend
 * API compatible avec localStorage mais persistant et robuste
 */
export const useDocumentStorage = () => {
  const [isReady, setIsReady] = useState(false);
  const dbRef = useRef(null);
  const migrationDoneRef = useRef(false);

  // Initialisation de la base de donn√©es IndexedDB
  const initDB = useCallback(async () => {
    if (dbRef.current) return dbRef.current;

    return new Promise((resolve, reject) => {
      console.log('üóÑÔ∏è Initialisation IndexedDB pour documents...');

      const request = indexedDB.open(DB_NAME, DB_VERSION);

      request.onerror = () => {
        console.error('‚ùå Erreur ouverture IndexedDB:', request.error);
        reject(request.error);
      };

      request.onsuccess = () => {
        console.log('‚úÖ IndexedDB initialis√©e avec succ√®s');
        dbRef.current = request.result;
        setIsReady(true);
        resolve(request.result);
      };

      request.onupgradeneeded = (event) => {
        console.log('üîß Cr√©ation/mise √† jour structure IndexedDB...');
        const db = event.target.result;

        // Cr√©er l'object store "documents" s'il n'existe pas
        if (!db.objectStoreNames.contains(DOCUMENTS_STORE)) {
          const store = db.createObjectStore(DOCUMENTS_STORE, { keyPath: 'key' });
          console.log('üìÅ Object store "documents" cr√©√©');
        }
      };
    });
  }, []);

  // Migration des donn√©es localStorage vers IndexedDB (une seule fois)
  const migrateFromLocalStorage = useCallback(async (db) => {
    if (migrationDoneRef.current) return;

    console.log('üîÑ Migration localStorage ‚Üí IndexedDB...');

    const transaction = db.transaction([DOCUMENTS_STORE], 'readwrite');
    const store = transaction.objectStore(DOCUMENTS_STORE);

    // Lister les cl√©s importantes √† migrer
    const keysToMigrate = [
      'editorContent',
      'editorViewMode',
      'lastSaved'
    ];

    let migratedCount = 0;

    for (const key of keysToMigrate) {
      const localValue = localStorage.getItem(key);
      if (localValue !== null) {
        try {
          await new Promise((resolve, reject) => {
            const putRequest = store.put({
              key: key,
              value: localValue,
              migratedFrom: 'localStorage',
              migratedAt: new Date().toISOString()
            });
            putRequest.onsuccess = () => resolve();
            putRequest.onerror = () => reject(putRequest.error);
          });
          migratedCount++;
          console.log(`‚úÖ Migr√©: ${key}`);
        } catch (error) {
          console.warn(`‚ö†Ô∏è √âchec migration ${key}:`, error);
        }
      }
    }

    console.log(`üéâ Migration termin√©e: ${migratedCount} √©l√©ments migr√©s`);
    migrationDoneRef.current = true;

    return transaction.complete;
  }, []);

  // Fonction getItem compatible localStorage
  const getItem = useCallback(async (key) => {
    try {
      const db = await initDB();

      // Migration automatique au premier acc√®s
      if (!migrationDoneRef.current) {
        await migrateFromLocalStorage(db);
      }

      return new Promise((resolve, reject) => {
        const transaction = db.transaction([DOCUMENTS_STORE], 'readonly');
        const store = transaction.objectStore(DOCUMENTS_STORE);
        const request = store.get(key);

        request.onsuccess = () => {
          const result = request.result;
          const value = result ? result.value : null;
          console.log(`üìñ getItem("${key}"):`, value ? `${value.length} chars` : 'null');
          resolve(value);
        };

        request.onerror = () => {
          console.error(`‚ùå Erreur getItem("${key}"):`, request.error);
          // Fallback vers localStorage en cas d'erreur
          const fallbackValue = localStorage.getItem(key);
          console.log(`üîÑ Fallback localStorage pour "${key}":`, fallbackValue ? 'trouv√©' : 'null');
          resolve(fallbackValue);
        };
      });
    } catch (error) {
      console.error('‚ùå Erreur IndexedDB getItem, fallback localStorage:', error);
      return localStorage.getItem(key);
    }
  }, [initDB, migrateFromLocalStorage]);

  // Fonction setItem compatible localStorage
  const setItem = useCallback(async (key, value) => {
    try {
      const db = await initDB();

      return new Promise((resolve, reject) => {
        const transaction = db.transaction([DOCUMENTS_STORE], 'readwrite');
        const store = transaction.objectStore(DOCUMENTS_STORE);

        const data = {
          key: key,
          value: value,
          updatedAt: new Date().toISOString()
        };

        const request = store.put(data);

        request.onsuccess = () => {
          console.log(`üíæ setItem("${key}"):`, value ? `${value.length} chars sauv√©s` : 'null sauv√©');
          resolve();
        };

        request.onerror = () => {
          console.error(`‚ùå Erreur setItem("${key}"):`, request.error);
          // Fallback vers localStorage en cas d'erreur
          localStorage.setItem(key, value);
          console.log(`üîÑ Fallback localStorage pour "${key}" effectu√©`);
          resolve();
        };
      });
    } catch (error) {
      console.error('‚ùå Erreur IndexedDB setItem, fallback localStorage:', error);
      localStorage.setItem(key, value);
    }
  }, [initDB]);

  // Fonction removeItem pour compl√©ter l'API
  const removeItem = useCallback(async (key) => {
    try {
      const db = await initDB();

      return new Promise((resolve, reject) => {
        const transaction = db.transaction([DOCUMENTS_STORE], 'readwrite');
        const store = transaction.objectStore(DOCUMENTS_STORE);
        const request = store.delete(key);

        request.onsuccess = () => {
          console.log(`üóëÔ∏è removeItem("${key}") effectu√©`);
          resolve();
        };

        request.onerror = () => {
          console.error(`‚ùå Erreur removeItem("${key}"):`, request.error);
          localStorage.removeItem(key);
          resolve();
        };
      });
    } catch (error) {
      console.error('‚ùå Erreur IndexedDB removeItem, fallback localStorage:', error);
      localStorage.removeItem(key);
    }
  }, [initDB]);

  return {
    getItem,
    setItem,
    removeItem,
    isReady
  };
};
