import React from 'react';
import { htmlToMarkdown, markdownToHtml } from '../utils/markdownConverter';
import {
  isFileSystemAccessSupported,
  initializePromptoDysWorkspace,
  createNewPromptoDysProject,
  saveImageToPromptoDys,
  saveMarkdownToPromptoDys,
  getCurrentProject,
  setCurrentProject,
  resetCurrentProject,
  generateTimestampFolderName
} from '../utils/promptoDysManager';

const Toolbar = ({ 
  currentFormat, 
  onFormatChange, 
  editorRef, 
  viewMode, 
  onViewModeChange, 
  content, 
  setContent, 
  onThemeSettingsToggle 
}) => {
  // État pour le workspace PromptoDYS
  const [promptoDysWorkspace, setPromptoDysWorkspace] = React.useState(null);
  const [isPromptoDysSupported] = React.useState(isFileSystemAccessSupported());

  // Fonctions pour les fichiers Markdown
  const handleCopyMarkdown = async () => {
    try {
      const markdownContent = content;

      // Enlever les attributs de couleur HTML pour avoir du Markdown natif
      markdownContent = markdownContent.replace(/<span style="color: [^"]+;">(.*?)<\/span>/gi, '$1');

      await navigator.clipboard.writeText(markdownContent);
      alert('Markdown copié dans le presse-papier !');
    } catch (error) {
      console.error('Erreur lors de la copie:', error);
      alert('Erreur lors de la copie du Markdown');
    }
  };

  const handleSaveMarkdown = async () => {
    try {
      const htmlContent = editorRef.current?.innerHTML || content;
      const markdownContent = htmlToMarkdown(htmlContent);

      // Essayer de sauvegarder dans PromptoDYS si un projet est actif
      const currentProject = getCurrentProject();
      if (isPromptoDysSupported && currentProject.directory) {
        try {
          const fileName = currentProject.name || generateTimestampFolderName();
          const success = await saveMarkdownToPromptoDys(markdownContent, currentProject.directory, fileName);
          if (success) {
            alert(`✅ Fichier sauvegardé dans PromptoDYS: ${fileName}.md`);
            return;
          }
        } catch (error) {
          console.warn('⚠️ Échec sauvegarde PromptoDYS, fallback classique:', error);
        }
      }

      // Fallback : sauvegarde classique
      const fileName = prompt('Nom du fichier (sans extension):', 'document');
      if (!fileName) return;

      const blob = new Blob([markdownContent], { type: 'text/markdown' });
      const url = URL.createObjectURL(blob);

      const link = document.createElement('a');
      link.href = url;
      link.download = `${fileName}.md`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Erreur lors de la sauvegarde:', error);
      alert('Erreur lors de la sauvegarde du fichier');
    }
  };

  const handleLoadMarkdown = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const markdownContent = e.target.result;
        const htmlContent = markdownToHtml(markdownContent);

        if (editorRef.current) {
          editorRef.current.innerHTML = htmlContent;
          // Déclencher l'événement de changement pour mettre à jour l'état
          const inputEvent = new Event('input', { bubbles: true });
          editorRef.current.dispatchEvent(inputEvent);
        }

        // Basculer en mode WYSIWYG après le chargement
        onViewModeChange('wysiwyg');
        alert('Fichier Markdown chargé avec succès !');
      } catch (error) {
        console.error('Erreur lors du chargement:', error);
        alert('Erreur lors du chargement du fichier Markdown');
      }
    };

    reader.readAsText(file);
    // Reset l'input pour permettre de recharger le même fichier
    event.target.value = '';
  };

  const colors = [
    { name: 'Noir', value: '#000000' },
    { name: 'Bleu', value: '#3399ff' },
    { name: 'Vert', value: '#00cc00' },
    { name: 'Rouge', value: '#ff0000' },
    { name: 'Orange', value: '#ff9900' },
    { name: 'Violet', value: '#ff00ff' }
  ];


  const execCommand = (command, value = null) => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();
    document.execCommand(command, false, value);

    // Mise à jour immédiate de l'état des boutons
    const newFormat = { ...currentFormat };

    if (command === 'bold') {
      newFormat.bold = !currentFormat.bold;
    } else if (command === 'italic') {
      newFormat.italic = !currentFormat.italic;
    } else if (command === 'foreColor') {
      newFormat.color = value;
    }

    // Forcer la mise à jour immédiate
    onFormatChange(newFormat);

    // Sauvegarder l'état pour les débuts de ligne
    onFormatChange(newFormat);
  };

  const handleHeading = (level) => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    // Nettoyer le formatage existant avant d'appliquer le titre
    document.execCommand('removeFormat', false, null);

    // Appliquer le titre
    document.execCommand('formatBlock', false, level);

    onFormatChange();
  };

  const handleTextFormat = () => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    // Désactiver les listes d'abord si elles sont actives
    if (document.queryCommandState('insertUnorderedList')) {
      document.execCommand('insertUnorderedList', false, null);
    }
    if (document.queryCommandState('insertOrderedList')) {
      document.execCommand('insertOrderedList', false, null);
    }

    // Force désactivation gras/italique AVANT removeFormat
    if (document.queryCommandState('bold')) {
      document.execCommand('bold', false, null);
    }
    if (document.queryCommandState('italic')) {
      document.execCommand('italic', false, null);
    }

    // Forcer d'abord le retour au paragraphe normal AVANT removeFormat
    document.execCommand('formatBlock', false, 'p');

    // Puis nettoyer le formatage existant
    document.execCommand('removeFormat', false, null);

    // État explicite Normal pour mise à jour immédiate
    const newFormat = {
      bold: false,
      italic: false,
      color: '#000000',
      fontSize: '16px',
      fontFamily: 'system-ui',
      heading: null,
      list: null
    };

    onFormatChange(newFormat);
  };

  const handleList = (type) => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    if (type === 'bullet') {
      document.execCommand('insertUnorderedList', false, null);
    } else if (type === 'number') {
      document.execCommand('insertOrderedList', false, null);
    } else if (type === 'letter') {
      document.execCommand('insertOrderedList', false, null);
      // Appliquer le style alphabétique à la liste créée
      const selection = window.getSelection();
      if (selection.rangeCount > 0) {
        let element = selection.getRangeAt(0).commonAncestorContainer;
        while (element && element.nodeType !== 1) {
          element = element.parentNode;
        }
        while (element && element.tagName !== 'OL') {
          element = element.parentNode;
        }
        if (element && element.tagName === 'OL') {
          element.style.listStyleType = 'lower-alpha';
        }
      }
    }

    onFormatChange();
  };

  const handleClearAll = () => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();

    // Sélectionner tout le contenu puis le supprimer
    document.execCommand('selectAll', false, null);
    document.execCommand('delete', false, null);

    onFormatChange();
  };

  const handleCopy = () => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();
    document.execCommand('copy', false, null);
  };

  const handlePaste = () => {
    if (viewMode !== 'wysiwyg' || !editorRef.current) return;

    editorRef.current.focus();
    document.execCommand('paste', false, null);

    // Forcer la mise à jour après collage
    setTimeout(() => onFormatChange(), 10);
  };

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file || !file.type.startsWith('image/')) {
      alert('Veuillez sélectionner un fichier image valide.');
      return;
    }

    // Vérifier la taille (max 5MB pour les fichiers PromptoDYS)
    if (file.size > 5 * 1024 * 1024) {
      alert('Image trop volumineuse. Taille maximum : 5MB');
      return;
    }

    // Essayer d'utiliser PromptoDYS si supporté
    if (isPromptoDysSupported) {
      try {
        // Initialiser le workspace PromptoDYS si nécessaire
        if (!promptoDysWorkspace) {
          const workspace = await initializePromptoDysWorkspace();
          if (workspace) {
            setPromptoDysWorkspace(workspace);
          }
        }

        // Obtenir ou créer le projet actuel
        let currentProject = getCurrentProject();
        if (!currentProject.directory && promptoDysWorkspace) {
          const projectHandle = await createNewPromptoDysProject(promptoDysWorkspace);
          if (projectHandle) {
            currentProject = getCurrentProject();
          }
        }

        // Sauvegarder l'image dans PromptoDYS
        if (currentProject.directory) {
          const imagePath = await saveImageToPromptoDys(file, currentProject.directory);
          if (imagePath && viewMode === 'wysiwyg' && editorRef.current) {
            editorRef.current.focus();
            const imgHtml = `<img src="${imagePath}" style="max-width: 100%; height: auto; margin: 0.5em 0;" alt="${file.name}" />`;
            document.execCommand('insertHTML', false, imgHtml);
            onFormatChange();
            console.log('✅ Image sauvegardée dans PromptoDYS:', imagePath);
            event.target.value = '';
            return;
          }
        }
      } catch (error) {
        console.warn('⚠️ Échec PromptoDYS, fallback vers base64:', error);
      }
    }

    // Fallback : utiliser base64 comme avant
    const reader = new FileReader();
    reader.onload = (e) => {
      if (viewMode === 'wysiwyg' && editorRef.current) {
        editorRef.current.focus();
        const imgHtml = `<img src="${e.target.result}" style="max-width: 100%; height: auto; margin: 0.5em 0;" alt="${file.name}" />`;
        document.execCommand('insertHTML', false, imgHtml);
        onFormatChange();
      }
    };

    reader.readAsDataURL(file);
    event.target.value = '';
  };


  return (
    <div className="border-4 border-blue-600 rounded-lg bg-blue-100 py-3 shadow-lg w-full">
      {/* Ligne 1 - Format + Couleurs + Mode */}
      <div className="flex items-center justify-start space-x-6 mb-2 w-full">
        {/* Format buttons */}
        <div className="flex items-center space-x-2">
          <span className="text-sm font-medium">Format:</span>
          <button
            onClick={() => handleTextFormat()}
            className={`px-2 py-0.5 text-xs rounded ${
              !currentFormat.bold && !currentFormat.italic && !currentFormat.heading && !currentFormat.list
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Normal
          </button>
          <button
            onClick={() => execCommand('bold')}
            className={`px-2 py-0.5 text-xs rounded font-bold ${
              currentFormat.bold
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Gras
          </button>
          <button
            onClick={() => execCommand('italic')}
            className={`px-2 py-0.5 text-xs rounded italic ${
              currentFormat.italic
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Italique
          </button>
        </div>

        {/* Colors (sans libellé) */}
        <div className="flex space-x-1">
          {colors.map((color, index) => (
            <button
              key={index}
              onClick={() => execCommand('foreColor', color.value)}
              className={`w-4 h-4 rounded-full border-2 ${
                currentFormat.color === color.value
                  ? 'border-blue-500 ring-2 ring-blue-300'
                  : 'border-gray-300 hover:border-gray-500'
              }`}
              style={{ backgroundColor: color.value }}
              disabled={viewMode !== 'wysiwyg'}
              title={color.name}
            />
          ))}
        </div>


        {/* Mode selection menu */}
        <div className="flex items-center space-x-2 mb-4">
          <span className="text-sm font-medium text-gray-700">Mode:</span>
          <select
            value={viewMode}
            onChange={(e) => onViewModeChange(e.target.value)}
            className="px-3 py-1 text-sm rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          >
            <option value="wysiwyg">📝 Éditeur visuel</option>
            <option value="markdown">#️⃣ Code Markdown</option>
            <option value="html">🌐 Code HTML</option>
          </select>
        </div>

        {/* Fichiers Markdown */}
        <div className="flex items-center space-x-2 mb-4">
          <span className="text-sm font-medium text-gray-700">Fichier:</span>
          <div className="relative group">
            <button
              onClick={handleSaveMarkdown}
              className="border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500 px-2 py-1 text-xs rounded"
              title={isPromptoDysSupported ? "Sauvegarde dans PromptoDYS ou téléchargement" : "Télécharger le fichier"}
            >
              💾 Sauvegarder
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Sauvegarder en .md
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={() => document.getElementById('markdown-input').click()}
              className="border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500 px-2 py-1 text-xs rounded"
            >
              📂 Ouvrir
            </button>
            <input
              id="markdown-input"
              type="file"
              accept=".md,.markdown"
              onChange={handleLoadMarkdown}
              className="hidden"
            />
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Charger un fichier .md
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          {isPromptoDysSupported && (
            <div className="relative group">
              <button
                onClick={() => {
                  resetCurrentProject();
                  setPromptoDysWorkspace(null);
                  alert('✅ Nouveau projet PromptoDYS. Prochaine image créera un nouveau dossier.');
                }}
                className="border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500 px-2 py-1 text-xs rounded"
                title="Commencer un nouveau projet PromptoDYS"
              >
                🆕 Nouveau
              </button>
              <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
                Nouveau projet PromptoDYS
                <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
              </div>
            </div>
          )}
        </div>

        {/* Bouton Thème */}
        <div className="flex items-center space-x-2 mb-4">
          <div className="relative group">
            <button
              onClick={onThemeSettingsToggle}
              className="px-3 py-1 text-sm rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500 font-medium"
            >
              🎨 Thème
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Paramètres du thème
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
        </div>

      </div>

      {/* Ligne 2 - Titres + Listes + Édition */}
      <div className="flex items-center justify-start space-x-6 w-full">
        {/* Headings */}
        <div className="flex items-center space-x-2">
          <span className="text-sm font-medium">Titres:</span>
          <button
            onClick={() => handleTextFormat()}
            className={`px-2 py-0.5 text-xs rounded ${
              !currentFormat.heading && !currentFormat.list
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Texte
          </button>
          <button
            onClick={() => handleHeading('h1')}
            className={`px-2 py-0.5 text-xs rounded ${
              currentFormat.heading === 'h1'
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Titre 1
          </button>
          <button
            onClick={() => handleHeading('h2')}
            className={`px-2 py-0.5 text-xs rounded ${
              currentFormat.heading === 'h2'
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Titre 2
          </button>
          <button
            onClick={() => handleHeading('h3')}
            className={`px-2 py-0.5 text-xs rounded ${
              currentFormat.heading === 'h3'
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            Titre 3
          </button>
        </div>

        {/* Lists */}
        <div className="flex items-center space-x-2">
          <span className="text-sm font-medium">Listes:</span>
          <button
            onClick={() => handleList('bullet')}
            className={`px-2 py-0.5 text-xs rounded ${
              currentFormat.list === 'bullet'
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            • Puces
          </button>
          <button
            onClick={() => handleList('number')}
            className={`px-2 py-0.5 text-xs rounded ${
              currentFormat.list === 'number'
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            1. Numéros
          </button>
          <button
            onClick={() => handleList('letter')}
            className={`px-2 py-0.5 text-xs rounded ${
              currentFormat.list === 'letter'
                ? 'border-2 border-blue-500 bg-blue-50 text-blue-700'
                : 'border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500'
            }`}
            disabled={viewMode !== 'wysiwyg'}
          >
            a. Lettres
          </button>
        </div>

        {/* Boutons Undo/Redo + Copier */}
        <div className="flex items-center space-x-2">
          <span className="text-sm font-medium text-gray-700">Édition:</span>
          <div className="relative group">
            <button
              onClick={() => document.execCommand('undo', false, null)}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
              disabled={viewMode !== 'wysiwyg'}
            >
              ↶
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Annuler (Ctrl+Z)
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={() => document.execCommand('redo', false, null)}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
              disabled={viewMode !== 'wysiwyg'}
            >
              ↷
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Rétablir (Ctrl+Y)
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={handleCopy}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
              disabled={viewMode !== 'wysiwyg'}
            >
              📄
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Copier (Ctrl+C)
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={handlePaste}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
              disabled={viewMode !== 'wysiwyg'}
            >
              📋
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Coller (Ctrl+V)
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={() => document.getElementById('image-input').click()}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
              disabled={viewMode !== 'wysiwyg'}
            >
              📷
            </button>
            <input
              id="image-input"
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="hidden"
            />
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Insérer une image
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={handleClearAll}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
              disabled={viewMode !== 'wysiwyg'}
            >
              🧽
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Effacer tout
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
          <div className="relative group">
            <button
              onClick={handleCopyMarkdown}
              className="px-2 py-0.5 text-xs rounded border border-gray-400 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-500"
            >
              📝
            </button>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-sm rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
              Copier en Markdown
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 -mt-1 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Toolbar;
